import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [existingDetails, setExistingDetails] = useState([]);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // Detail data: day-wise images
  const [daysCount, setDaysCount] = useState(1);
  const [daysData, setDaysData] = useState([
    { dayNumber: 1, images: [] },
  ]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
  }, [formOpen, viewPackage, detailForm]);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleOpenDetailForm = async (pkg) => {
    await loadExistingDetails(pkg._id);
    setDetailForm(pkg);
  };

  const loadExistingDetails = async (packageId) => {
    try {
      setExistingDetails([]);
      const res = await fetch(`${apiURL}/package-details/packagebyid/${packageId}`);
      if (res.ok) {
        const payload = await res.json();
        setExistingDetails(Array.isArray(payload.data) ? payload.data : []);
      } else {
        setExistingDetails([]);
      }
    } catch (e) {
      console.warn("Could not fetch existing package details", e);
      setExistingDetails([]);
    }
  };

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Detail Form Logic
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const handleDaysChange = (count) => {
    const num = parseInt(count);
    setDaysCount(num);
    const newDays = Array.from({ length: num }, (_, i) => ({
      dayNumber: i + 1,
      images: [],
    }));
    setDaysData(newDays);
  };

  const handleAddImagesForDay = (dayIndex, files) => {
    const updated = [...daysData];
    const newImgs = Array.from(files).map((file) => ({
      image: file,
      imageName: file.name.replace(/\.[^/.]+$/, ""),
      rating: 4.5,
      review: "",
      imageDetail: "",
    }));
    updated[dayIndex].images.push(...newImgs);
    setDaysData(updated);
  };

  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) {
      alert("Package ID missing! Please re-open the detail form.");
      return;
    }

    const formData = new FormData();
    formData.append("packageID", detailForm._id);
    formData.append("daysCount", daysCount);

    const daysPayload = daysData.map((day) => ({
      dayNumber: day.dayNumber,
      images: day.images.map((img) => ({
        imageName: img.imageName,
        rating: Number(img.rating) || 4.5,
        review: img.review,
        imageDetail: img.imageDetail,
      })),
    }));

    formData.append("daysData", JSON.stringify(daysPayload));

    // Append all files
    daysData.forEach((day) => {
      day.images.forEach((img) => {
        formData.append("images", img.image);
      });
    });

    try {
      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) {
        alert("‚ùå Failed to save details: " + (data.message || "Unknown error"));
        return;
      }

      alert("‚úÖ Package Details Saved Successfully!");
      await loadExistingDetails(detailForm._id);
    } catch (error) {
      console.error("Error saving details:", error);
      alert("Something went wrong while saving package details.");
    }
  };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // JSX
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Package Grid */}
      <div className="package-grid">
        {packages.map((pkg) => (
          <div key={pkg._id} className="package-card">
            {pkg.image && <img src={pkg.image} alt={pkg.title} className="package-img" />}
            <div className="package-info">
              <h3>{pkg.title}</h3>
              <p>üè∑Ô∏è {pkg.category?.title}</p>
              <p>üåç {pkg.country}</p>
              <p>üí∞ ‚Çπ{pkg.price}</p>
              <p>‚≠ê {pkg.rating}</p>
              <p>Review: {pkg.review}</p>
              <p>üè† Show on Home: {pkg.showOnHome ? "‚úÖ Yes" : "‚ùå No"}</p>
              <div className="card-actions">
                <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>
                  ‚úè Edit
                </button>
                <button className="remove-btn" onClick={() => handleRemovePackage(pkg._id)}>
                  ‚ùå Remove
                </button>
                <button className="view-btn" onClick={() => handleOpenDetailForm(pkg)}>
                  üìã Detail
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Detail Form Modal */}
      {detailForm && (
        <div className="modal-overlay">
          <div className="modal" style={{ maxWidth: "950px", maxHeight: "85vh", overflowY: "auto" }}>
            <h2>üìã Add Package Details</h2>

            <div className="days-section">
              <label>Number of Days:</label>
              <input
                type="number"
                min="1"
                value={daysCount}
                onChange={(e) => handleDaysChange(e.target.value)}
                style={{ width: "100px", marginLeft: "10px" }}
              />
            </div>

            {daysData.map((day, dIndex) => (
              <div key={dIndex} className="day-block">
                <h3>üóìÔ∏è Day {day.dayNumber}</h3>

                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => handleAddImagesForDay(dIndex, e.target.files)}
                />

                {day.images.map((img, iIndex) => (
                  <div key={iIndex} className="image-block">
                    <input
                      type="text"
                      placeholder="Image Name"
                      value={img.imageName}
                      onChange={(e) => {
                        const updated = [...daysData];
                        updated[dIndex].images[iIndex].imageName = e.target.value;
                        setDaysData(updated);
                      }}
                    />
                    <input
                      type="number"
                      min="1"
                      max="5"
                      step="0.1"
                      placeholder="Rating"
                      value={img.rating}
                      onChange={(e) => {
                        const updated = [...daysData];
                        updated[dIndex].images[iIndex].rating = e.target.value;
                        setDaysData(updated);
                      }}
                    />
                    <input
                      type="text"
                      placeholder="Review"
                      value={img.review}
                      onChange={(e) => {
                        const updated = [...daysData];
                        updated[dIndex].images[iIndex].review = e.target.value;
                        setDaysData(updated);
                      }}
                    />
                    <textarea
                      placeholder="Image Detail"
                      value={img.imageDetail}
                      onChange={(e) => {
                        const updated = [...daysData];
                        updated[dIndex].images[iIndex].imageDetail = e.target.value;
                        setDaysData(updated);
                      }}
                    ></textarea>
                  </div>
                ))}
              </div>
            ))}

            <div className="modal-actions">
              <button className="save-btn" onClick={handleSavePackageDetail}>‚úÖ Save</button>
              <button className="cancel-btn" onClick={() => setDetailForm(null)}>‚ùå Close</button>
            </div>

            {/* Existing Details */}
            <div style={{ marginTop: 20 }}>
              <h3>üìö Existing Details</h3>
              {existingDetails.length === 0 ? (
                <p>No details added yet.</p>
              ) : (
                existingDetails.map((det) => (
                  <div key={det._id} style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "10px" }}>
                    <p><b>Day:</b> {det.dayNumber}</p>
                    <p><b>Image:</b> {det.imageName}</p>
                    <p><b>Rating:</b> {det.rating}</p>
                    <p><b>Review:</b> {det.review}</p>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
