import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // ğŸŒ Detail form fields
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: 1,
    dayDetails: [],
  });

  /* Disable scroll when any modal open */
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  /* Fetch Data */
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  /* Add or Update Package */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("âœ… Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* Delete Package */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* Edit */
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* View */
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  /* Reset */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  /* Filter packages */
  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  // ğŸ§© Detail form logic
  const handleDaysChange = (e) => {
    const days = parseInt(e.target.value);
    const updatedDetails = [...detailData.dayDetails];

    while (updatedDetails.length < days) {
      updatedDetails.push({ images: [], imageDetails: "", rating: 0, review: "" });
    }
    while (updatedDetails.length > days) {
      updatedDetails.pop();
    }

    setDetailData({ ...detailData, days, dayDetails: updatedDetails });
  };

  const handleImageChange = (dayIndex, files) => {
    const updatedDetails = [...detailData.dayDetails];
    updatedDetails[dayIndex].images = Array.from(files);
    setDetailData({ ...detailData, dayDetails: updatedDetails });
  };

  const handleDetailChange = (dayIndex, field, value) => {
    const updatedDetails = [...detailData.dayDetails];
    updatedDetails[dayIndex][field] = value;
    setDetailData({ ...detailData, dayDetails: updatedDetails });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>ğŸŒ Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          â• Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>ğŸ·ï¸ {pkg.category?.title}</p>
                <p className="country-text">ğŸŒ {pkg.country}</p>
                <p>ğŸ’° â‚¹{pkg.price}</p>
                <p>â­ {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>ğŸ  Show on Home: {pkg.showOnHome ? "âœ… Yes" : "âŒ No"}</p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    âœ Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    âŒ Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    ğŸ‘ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid" style={{ maxHeight: "70vh", overflowY: "auto" }}>
              {/* Show on Home */}
              <div className="radio-group" style={{ gridColumn: "span 2" }}>
                <label>Show on Home:</label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === true}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: true })
                    }
                  />
                  Yes
                </label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === false}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: false })
                    }
                  />
                  No
                </label>
              </div>

              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />
              <select
                value={newPackage.type}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, type: e.target.value })
                }
              >
                <option value="">Select Type</option>
                <option value="Domestic">Domestic</option>
                <option value="Overseas">Overseas</option>
              </select>
              <input
                type="text"
                placeholder="Country"
                value={newPackage.country}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, country: e.target.value })
                }
              />
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                <option value="">Select Subcategory</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.title}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    rating: parseFloat(e.target.value),
                  })
                }
              />
              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, review: e.target.value })
                }
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files[0])}
              />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "âœ… Update" : "â• Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                âŒ Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>ğŸ‘ View Package</h2>
            {viewPackage.image && (
              <img src={viewPackage.image} alt={viewPackage.title} className="package-img" />
            )}
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> â‚¹{viewPackage.price}</p>
            <p><strong>Rating:</strong> â­ {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "âœ… Yes" : "âŒ No"}
            </p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  setDetailForm(viewPackage);
                }}
              >
                ğŸ“‹ Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                âŒ Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Form */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{
              maxWidth: "700px",
              maxHeight: "80vh",
              overflowY: "auto",
            }}
          >
            <h2>ğŸ“‹ Package Detail Form</h2>

            <div
              className="form-grid"
              style={{ gridTemplateColumns: "1fr 1fr" }}
            >
              {/* ğŸï¸ Main Image Section */}
              <h3 style={{ gridColumn: "span 2", marginTop: "10px" }}>
                ğŸï¸ Main Image
              </h3>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  setDetailData({ ...detailData, mainImage: e.target.files[0] })
                }
              />

              {/* ğŸ“… Trip Duration */}
              <h3 style={{ gridColumn: "span 2", marginTop: "20px" }}>
                ğŸ“… Trip Duration
              </h3>
              <input
                type="number"
                min="1"
                placeholder="Number of Days"
                value={detailData.days}
                onChange={handleDaysChange}
              />

              {/* Dynamic Days */}
              {detailData.dayDetails.map((day, index) => (
                <div
                  key={index}
                  style={{
                    gridColumn: "span 2",
                    border: "1px solid #ccc",
                    borderRadius: "10px",
                    padding: "10px",
                    marginBottom: "12px",
                  }}
                >
                  {/* ğŸ—“ï¸ Heading for each day */}
                  <h3 style={{ marginBottom: "10px" }}>
                    ğŸ—“ï¸ Day {index + 1} Section
                  </h3>

                  <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={(e) => handleImageChange(index, e.target.files)}
                  />
                  <input
                    type="text"
                    placeholder="Images Name"
                    value={day.imagesName || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "imagesName", e.target.value)
                    }
                  />
                  <input
                    type="number"
                    placeholder="Rating"
                    value={day.rating || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "rating", e.target.value)
                    }
                  />
                  <input
                    type="number"
                    placeholder="Review"
                    value={day.review || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "review", e.target.value)
                    }
                  />
                  <textarea
                    placeholder="Image Details"
                    value={day.imageDetails || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "imageDetails", e.target.value)
                    }
                  />
                </div>
              ))}
            </div>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  alert("Detail saved successfully! ğŸ‰");
                  setDetailForm(null);
                }}
              >
                âœ… Save
              </button>
              <button className="cancel-btn" onClick={() => setDetailForm(null)}>
                âŒ Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
