import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  // üÜï Detail Form State
  const [detailFormOpen, setDetailFormOpen] = useState(false);
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: 1,
    rating: 0,
    review: 0,
    dayWiseImages: [{ dayNumber: 1, images: [], imageNames: [] }],
  });

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  /* Disable scroll when modal open */
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailFormOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailFormOpen]);

  /* Fetch Data */
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  /* Add or Update Package */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        if (newPackage[key] !== undefined && newPackage[key] !== null) {
          formData.append(key, newPackage[key]);
        }
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok)
        return alert("‚ùå Error: " + (data.message || "Failed to save package"));

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* Delete Package */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* Edit */
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* View */
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  /* Open Detail Form */
  const handleOpenDetailForm = () => {
    setDetailFormOpen(true);
    setDetailData({
      mainImage: null,
      days: 1,
      rating: 0,
      review: 0,
      dayWiseImages: [{ dayNumber: 1, images: [], imageNames: [] }],
    });
  };

  /* Handle Days Change */
  const handleDaysChange = (days) => {
    const num = parseInt(days);
    const updated = [];
    for (let i = 1; i <= num; i++) {
      updated.push({ dayNumber: i, images: [], imageNames: [] });
    }
    setDetailData({ ...detailData, days: num, dayWiseImages: updated });
  };

  /* Save Details */
  const handleSaveDetails = async () => {
    const formData = new FormData();
    formData.append("days", detailData.days);
    formData.append("rating", detailData.rating);
    formData.append("review", detailData.review);

    if (detailData.mainImage) formData.append("mainImage", detailData.mainImage);

    detailData.dayWiseImages.forEach((day, idx) => {
      formData.append(`dayWiseImages[${idx}][dayNumber]`, day.dayNumber);
      day.images.forEach((img) =>
        formData.append(`dayWiseImages[${idx}][images]`, img)
      );
      day.imageNames.forEach((name) =>
        formData.append(`dayWiseImages[${idx}][imageNames]`, name)
      );
    });

    try {
      const response = await fetch(`${apiURL}/packages/details/${viewPackage._id}`, {
        method: "PUT",
        body: formData,
      });
      if (!response.ok) throw new Error("Failed to save details");
      alert("‚úÖ Details saved successfully!");
      setDetailFormOpen(false);
      fetchPackages();
    } catch (err) {
      console.error(err);
      alert("Error saving details.");
    }
  };

  /* Reset */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p className="country-text">üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>üè† Show on Home: {pkg.showOnHome ? "‚úÖ Yes" : "‚ùå No"}</p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    ‚úè Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    ‚ùå Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    üëÅ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{viewPackage.title}</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p>
              <strong>Main Category:</strong> {viewPackage.type}
            </p>
            <p>
              <strong>Subcategory:</strong> {viewPackage.category?.title}
            </p>
            <p>
              <strong>Country:</strong> {viewPackage.country}
            </p>
            <p>
              <strong>Price:</strong> ‚Çπ{viewPackage.price}
            </p>
            <p>
              <strong>Rating:</strong> {viewPackage.rating}
            </p>
            <p>
              <strong>Review:</strong> {viewPackage.review}
            </p>
            <div className="modal-actions">
              <button onClick={closeViewDetails} className="cancel-btn">
                ‚ùå Close
              </button>
              <button
                onClick={handleOpenDetailForm}
                className="save-btn"
                style={{ background: "#007bff" }}
              >
                üß© Details
              </button>
            </div>
          </div>
        </div>
      )}

      {/* üßæ Detail Form Modal */}
      {detailFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>üìã Package Details</h2>

            <div
              className="form-grid"
              style={{
                maxHeight: "70vh",
                overflowY: "auto",
                paddingRight: "10px",
              }}
            >
              {/* Main Image */}
              <label>Main Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  setDetailData({ ...detailData, mainImage: e.target.files[0] })
                }
              />

              {/* Days */}
              <label>Number of Days:</label>
              <input
                type="number"
                min="1"
                value={detailData.days}
                onChange={(e) => handleDaysChange(e.target.value)}
              />

              {/* Rating */}
              <label>Rating:</label>
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                value={detailData.rating}
                onChange={(e) =>
                  setDetailData({
                    ...detailData,
                    rating: parseFloat(e.target.value),
                  })
                }
              />

              {/* Review */}
              <label>Review (as number):</label>
              <input
                type="number"
                value={detailData.review}
                onChange={(e) =>
                  setDetailData({
                    ...detailData,
                    review: parseFloat(e.target.value),
                  })
                }
              />

              {/* Day-wise Uploads */}
              {detailData.dayWiseImages.map((day, dayIdx) => (
                <div
                  key={dayIdx}
                  className="day-section"
                  style={{
                    background: "#f9f9f9",
                    padding: "10px",
                    borderRadius: "8px",
                    marginBottom: "15px",
                  }}
                >
                  <h4>üìÖ Day {day.dayNumber}</h4>

                  {/* Upload multiple images */}
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) => {
                      const files = Array.from(e.target.files);
                      const updated = [...detailData.dayWiseImages];
                      updated[dayIdx].images = files;
                      updated[dayIdx].imageNames = files.map(() => "");
                      setDetailData({ ...detailData, dayWiseImages: updated });
                    }}
                  />

                  {/* Multiple image names */}
                  {day.images.length > 0 && (
                    <div
                      style={{
                        display: "flex",
                        flexDirection: "column",
                        gap: "8px",
                        marginTop: "8px",
                      }}
                    >
                      {day.images.map((file, imgIdx) => (
                        <div
                          key={imgIdx}
                          style={{
                            display: "flex",
                            alignItems: "center",
                            gap: "10px",
                          }}
                        >
                          <label style={{ flex: "0 0 100px" }}>
                            üñºÔ∏è {file.name.slice(0, 20)}
                          </label>
                          <input
                            type="text"
                            placeholder="Enter image name"
                            value={day.imageNames[imgIdx] || ""}
                            onChange={(e) => {
                              const updated = [...detailData.dayWiseImages];
                              updated[dayIdx].imageNames[imgIdx] =
                                e.target.value;
                              setDetailData({
                                ...detailData,
                                dayWiseImages: updated,
                              });
                            }}
                            style={{ flex: 1 }}
                          />
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>

            <div className="modal-actions">
              <button onClick={handleSaveDetails} className="save-btn">
                üíæ Save Details
              </button>
              <button
                onClick={() => setDetailFormOpen(false)}
                className="cancel-btn"
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
