import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [existingDetails, setExistingDetails] = useState([]);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
    days: "", // ‚ú® NEW FIELD
  });

  // üíæ Detail Data
  const [detailData, setDetailData] = useState({
    mainImage: null,
    images: [],
  });

  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ ADD / UPDATE PACKAGE ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");
    if (!newPackage.days) return alert("Please enter number of Days!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
      days: "",
    });
  };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ DETAIL FORM ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const handleOpenDetailForm = async (pkg) => {
    await loadExistingDetails(pkg._id);
    setDetailForm(pkg);
  };

  const loadExistingDetails = async (packageId) => {
    try {
      setExistingDetails([]);
      const res = await fetch(`${apiURL}/package-details/packagebyid/${packageId}`);
      if (res.ok) {
        const payload = await res.json();
        setExistingDetails(Array.isArray(payload.data) ? payload.data : []);
      } else {
        setExistingDetails([]);
      }
    } catch (e) {
      console.warn("Could not fetch existing package details", e);
      setExistingDetails([]);
    }
  };

  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) {
      alert("Package ID missing! Please re-open the detail form.");
      return;
    }

    if (!detailData.images.length) {
      alert("Please upload at least one image with details.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("packageID", detailForm._id);

      const imagesPayload = detailData.images.map((img) => ({
        imageName: img.imageName,
        rating: Number(img.rating) || 4.5,
        review: img.review || "",
        imageDetail: img.imageDetail || "",
      }));

      formData.append("imagesData", JSON.stringify(imagesPayload));
      if (detailData.mainImage) formData.append("image", detailData.mainImage);
      detailData.images.forEach((img) => formData.append("images", img.image));

      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) {
        alert("‚ùå Failed to save details: " + (data.message || "Unknown error"));
        return;
      }

      alert("‚úÖ Package Detail saved successfully!");
      await loadExistingDetails(detailForm._id);
    } catch (error) {
      console.error("Error saving package detail:", error);
      alert("Something went wrong while saving the package detail.");
    }
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üïí {pkg.days} Days</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <div className="card-actions">
                  <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>
                    ‚úè Edit
                  </button>
                  <button className="remove-btn" onClick={() => handleRemovePackage(pkg._id)}>
                    ‚ùå Remove
                  </button>
                  <button className="view-btn" onClick={() => handleViewDetails(pkg)}>
                    üëÅ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid" style={{ maxHeight: "70vh", overflowY: "auto" }}>
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) => setNewPackage({ ...newPackage, title: e.target.value })}
              />
              <input
                type="text"
                placeholder="Country"
                value={newPackage.country}
                onChange={(e) => setNewPackage({ ...newPackage, country: e.target.value })}
              />
              <input
                type="number"
                placeholder="Days"
                value={newPackage.days}
                onChange={(e) => setNewPackage({ ...newPackage, days: e.target.value })}
              />
              <select
                value={newPackage.type}
                onChange={(e) => setNewPackage({ ...newPackage, type: e.target.value })}
              >
                <option value="">Select Type</option>
                <option value="Domestic">Domestic</option>
                <option value="Overseas">Overseas</option>
              </select>
              <select
                value={newPackage.category}
                onChange={(e) => setNewPackage({ ...newPackage, category: e.target.value })}
              >
                <option value="">Select Subcategory</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.title}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })}
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, rating: parseFloat(e.target.value) })
                }
              />
              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) => setNewPackage({ ...newPackage, review: e.target.value })}
              />
              <input type="file" accept="image/*" onChange={(e) => setImageFile(e.target.files[0])} />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>üëÅ View Package</h2>
            {viewPackage.image && (
              <img src={viewPackage.image} alt={viewPackage.title} className="package-img" />
            )}
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Days:</strong> {viewPackage.days}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> ‚Çπ{viewPackage.price}</p>
            <p><strong>Rating:</strong> ‚≠ê {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  handleOpenDetailForm(viewPackage);
                }}
              >
                üìã Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Form (Based on Days) */}
      {detailForm && (
        <div className="modal-overlay">
          <div className="modal" style={{ maxWidth: "900px", maxHeight: "85vh", overflowY: "auto" }}>
            <h2>üìã Package Detail Form</h2>
            <h3>üóìÔ∏è {detailForm.days} Days - Add Image Details</h3>

            <h3>üñºÔ∏è Main Image</h3>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setDetailData({ ...detailData, mainImage: e.target.files[0] })}
            />

            <h3 style={{ marginTop: 16 }}>üñºÔ∏è Upload {detailForm.days} Images</h3>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={(e) => {
                const files = Array.from(e.target.files).slice(0, detailForm.days);
                const updated = files.map((file, i) => ({
                  image: file,
                  imageName: `Day ${i + 1} - ${file.name.replace(/\.[^/.]+$/, "")}`,
                  rating: 4.5,
                  review: "",
                  imageDetail: `Details for Day ${i + 1}`,
                }));
                setDetailData({ ...detailData, images: updated });
              }}
            />

            {detailData.images.map((img, index) => (
              <div key={index} style={{ border: "1px solid #ccc", borderRadius: "10px", padding: "12px", marginTop: "12px" }}>
                <h4>üìÖ Day {index + 1}</h4>
                <input
                  type="text"
                  placeholder="Image Name"
                  value={img.imageName}
                  onChange={(e) => {
                    const updated = [...detailData.images];
                    updated[index].imageName = e.target.value;
                    setDetailData({ ...detailData, images: updated });
                  }}
                  style={{ width: "100%", marginBottom: 8 }}
                />
                <textarea
                  placeholder="Image Detail / Description"
                  value={img.imageDetail}
                  onChange={(e) => {
                    const updated = [...detailData.images];
                    updated[index].imageDetail = e.target.value;
                    setDetailData({ ...detailData, images: updated });
                  }}
                  style={{ width: "100%", minHeight: "60px" }}
                ></textarea>
              </div>
            ))}

            <div className="modal-actions" style={{ marginTop: 12 }}>
              <button className="save-btn" onClick={handleSavePackageDetail}>
                ‚úÖ Save
              </button>
              <button className="cancel-btn" onClick={() => setDetailForm(null)}>
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
