import React, { useState, useEffect } from "react";
import "./Packages.css";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    description: "",
    destination: "",
    category: "Domestic",
    duration: "",
    price: "",
    inclusions: "",
    exclusions: "",
    availability: "",
    rating: "",
    status: "Active",
    state: "",
    touristPlaces: "",
    images: "",
  });

  // Fetch packages
  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/packages");
      const data = await res.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("‚ùå Error fetching packages:", error);
    }
  };

  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.destination || !newPackage.price) {
      alert("‚ö†Ô∏è Title, destination, and price are required!");
      return;
    }

    try {
      const payload = {
        ...newPackage,
        price: Number(newPackage.price),
        rating: Number(newPackage.rating) || 0,
        inclusions: newPackage.inclusions.split(",").map((s) => s.trim()),
        exclusions: newPackage.exclusions.split(",").map((s) => s.trim()),
        touristPlaces: newPackage.touristPlaces
          .split(",")
          .map((s) => s.trim()),
        images: newPackage.images ? [newPackage.images] : [],
      };

      const res = await fetch(
        editingId
          ? `http://localhost:5000/api/packages/${editingId}`
          : "http://localhost:5000/api/packages",
        {
          method: editingId ? "PUT" : "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        }
      );

      const data = await res.json();

      if (data.result === "success") {
        alert("‚úÖ Package saved successfully!");
        fetchPackages();
        resetForm();
      } else {
        console.error("‚ùå Server error:", data.message);
        alert("‚ö†Ô∏è Something went wrong while saving the package.");
      }
    } catch (error) {
      console.error("‚ùå Network error:", error);
      alert("‚ö†Ô∏è Something went wrong while saving the package.");
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Delete this package?")) return;
    await fetch(`http://localhost:5000/api/packages/${id}`, { method: "DELETE" });
    fetchPackages();
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      touristPlaces: (pkg.touristPlaces || []).join(", "),
      inclusions: (pkg.inclusions || []).join(", "),
      exclusions: (pkg.exclusions || []).join(", "),
      images: (pkg.images && pkg.images[0]) || "",
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setNewPackage({
      title: "",
      description: "",
      destination: "",
      category: "Domestic",
      duration: "",
      price: "",
      inclusions: "",
      exclusions: "",
      availability: "",
      rating: "",
      status: "Active",
      state: "",
      touristPlaces: "",
      images: "",
    });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "International"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {packages
          .filter(
            (pkg) =>
              pkg.category?.toLowerCase() === activeTab.toLowerCase()
          )
          .map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.images?.[0] && (
                <img src={pkg.images[0]} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>{pkg.description}</p>
                <p><strong>üìç {pkg.destination}</strong></p>
                <p><strong>üí∞ ‚Çπ{pkg.price}</strong> ‚Ä¢ {pkg.duration}</p>
                <p><strong>‚≠ê</strong> {pkg.rating}</p>
                <div className="card-actions">
                  <button onClick={() => handleEditPackage(pkg)}>‚úèÔ∏è Edit</button>
                  <button onClick={() => handleRemovePackage(pkg._id)}>‚ùå Remove</button>
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid">
              <input type="text" placeholder="Title"
                value={newPackage.title}
                onChange={(e) => setNewPackage({ ...newPackage, title: e.target.value })}/>
              <input type="text" placeholder="Destination"
                value={newPackage.destination}
                onChange={(e) => setNewPackage({ ...newPackage, destination: e.target.value })}/>
              <textarea placeholder="Description"
                value={newPackage.description}
                onChange={(e) => setNewPackage({ ...newPackage, description: e.target.value })}/>
              <input type="text" placeholder="Duration"
                value={newPackage.duration}
                onChange={(e) => setNewPackage({ ...newPackage, duration: e.target.value })}/>
              <input type="number" placeholder="Price"
                value={newPackage.price}
                onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })}/>
              <input type="text" placeholder="Inclusions (comma separated)"
                value={newPackage.inclusions}
                onChange={(e) => setNewPackage({ ...newPackage, inclusions: e.target.value })}/>
              <input type="text" placeholder="Exclusions (comma separated)"
                value={newPackage.exclusions}
                onChange={(e) => setNewPackage({ ...newPackage, exclusions: e.target.value })}/>
              <input type="text" placeholder="Tourist Places (comma separated)"
                value={newPackage.touristPlaces}
                onChange={(e) => setNewPackage({ ...newPackage, touristPlaces: e.target.value })}/>
              <input type="text" placeholder="Image URL"
                value={newPackage.images}
                onChange={(e) => setNewPackage({ ...newPackage, images: e.target.value })}/>
              <input type="number" step="0.1" max="5" placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) => setNewPackage({ ...newPackage, rating: e.target.value })}/>
            </div>

            <div className="modal-actions">
              <button className="save-btn" onClick={handleAddOrUpdatePackage}>
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button className="cancel-btn" onClick={resetForm}>‚ùå Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
