import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [existingDetails, setExistingDetails] = useState([]);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // ğŸ’¾ Detail Data (for multiple images and days)
  const [detailData, setDetailData] = useState({
    mainImage: null,
    numDays: 1,
    days: [{ day: 1, images: [] }],
  });

  // Disable scroll when any modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  // Initial fetch
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // Add or Update Package
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("âœ… Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  // Delete Package
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  // Edit base package
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // Handle Multiple Image Details
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const handleOpenDetailForm = async (pkg) => {
    await loadExistingDetails(pkg._id);
    setDetailForm(pkg);
  };

  const loadExistingDetails = async (packageId) => {
    try {
      setExistingDetails([]);
      const res = await fetch(`${apiURL}/package-details/packagebyid/${packageId}`);
      if (res.ok) {
        const payload = await res.json();
        setExistingDetails(Array.isArray(payload.data) ? payload.data : []);
      } else {
        setExistingDetails([]);
      }
    } catch (e) {
      console.warn("Could not fetch existing package details", e);
      setExistingDetails([]);
    }
  };

  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) {
      alert("Package ID missing! Please re-open the detail form.");
      return;
    }

    const allImages = detailData.days.flatMap((d) => d.images);
    if (allImages.length === 0) {
      alert("Please upload images for at least one day.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("packageID", detailForm._id);

      const imagesPayload = [];
      detailData.days.forEach((dayBlock, i) => {
        dayBlock.images.forEach((img) => {
          imagesPayload.push({
            day: dayBlock.day,
            imageName: img.imageName,
            rating: Number(img.rating) || 4.5,
            review: img.review || "",
            imageDetail: img.imageDetail || "",
          });
          formData.append("images", img.image);
        });
      });

      formData.append("imagesData", JSON.stringify(imagesPayload));

      if (detailData.mainImage) formData.append("image", detailData.mainImage);

      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) {
        alert("âŒ Failed to save details: " + (data.message || "Unknown error"));
        return;
      }

      alert("âœ… Package Detail saved successfully!");
      await loadExistingDetails(detailForm._id);
    } catch (error) {
      console.error("Error saving package detail:", error);
      alert("Something went wrong while saving the package detail.");
    }
  };

  const handleDaysChange = (num) => {
    const newNum = parseInt(num);
    if (isNaN(newNum) || newNum <= 0) return;
    const updatedDays = [];
    for (let i = 1; i <= newNum; i++) {
      const existing = detailData.days.find((d) => d.day === i);
      updatedDays.push(existing || { day: i, images: [] });
    }
    setDetailData({ ...detailData, numDays: newNum, days: updatedDays });
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>ğŸŒ Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          â• Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>ğŸ·ï¸ {pkg.category?.title}</p>
                <p>ğŸŒ {pkg.country}</p>
                <p>ğŸ’° â‚¹{pkg.price}</p>
                <p>â­ {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>ğŸ  Show on Home: {pkg.showOnHome ? "âœ… Yes" : "âŒ No"}</p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    âœ Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    âŒ Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    ğŸ‘ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>ğŸ‘ View Package</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> â‚¹{viewPackage.price}</p>
            <p><strong>Rating:</strong> â­ {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "âœ… Yes" : "âŒ No"}
            </p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  handleOpenDetailForm(viewPackage);
                }}
              >
                ğŸ“‹ Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                âŒ Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Form */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{ maxWidth: "900px", maxHeight: "85vh", overflowY: "auto" }}
          >
            <h2>ğŸ“‹ Package Detail Form</h2>

            <h3>ğŸ–¼ï¸ Main Image</h3>
            <input
              type="file"
              accept="image/*"
              onChange={(e) =>
                setDetailData({ ...detailData, mainImage: e.target.files[0] })
              }
            />
            {detailData.mainImage && (
              <p style={{ fontSize: 13, color: "#666" }}>
                Selected: {detailData.mainImage.name}
              </p>
            )}

            <h3>ğŸ“… Number of Days</h3>
            <input
              type="number"
              min="1"
              placeholder="Enter number of days"
              value={detailData.numDays}
              onChange={(e) => handleDaysChange(e.target.value)}
            />

            {/* Loop Days */}
            {detailData.days.map((dayBlock, dayIndex) => (
              <div key={dayIndex} className="day-section">
                <h3>ğŸ—“ï¸ Day {dayBlock.day}</h3>

                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => {
                    const files = Array.from(e.target.files);
                    const imgs = files.map((file) => ({
                      image: file,
                      imageName: file.name.replace(/\.[^/.]+$/, ""),
                      rating: 4.5,
                      review: "",
                      imageDetail: "",
                    }));
                    const updatedDays = [...detailData.days];
                    updatedDays[dayIndex].images = imgs;
                    setDetailData({ ...detailData, days: updatedDays });
                  }}
                />

                {dayBlock.images.map((img, index) => (
                  <div
                    key={index}
                    className="image-box"
                  >
                    <h4>ğŸ–¼ï¸ Image {index + 1}</h4>
                    <p style={{ fontSize: 13, color: "#666" }}>{img.image.name}</p>

                    <input
                      type="text"
                      placeholder="Image Name"
                      value={img.imageName}
                      onChange={(e) => {
                        const updated = [...detailData.days];
                        updated[dayIndex].images[index].imageName = e.target.value;
                        setDetailData({ ...detailData, days: updated });
                      }}
                    />

                    <input
                      type="number"
                      min="1"
                      max="5"
                      step="0.1"
                      placeholder="Rating (1â€“5)"
                      value={img.rating}
                      onChange={(e) => {
                        const updated = [...detailData.days];
                        updated[dayIndex].images[index].rating = e.target.value;
                        setDetailData({ ...detailData, days: updated });
                      }}
                    />

                    <input
                      type="text"
                      placeholder="Review"
                      value={img.review}
                      onChange={(e) => {
                        const updated = [...detailData.days];
                        updated[dayIndex].images[index].review = e.target.value;
                        setDetailData({ ...detailData, days: updated });
                      }}
                    />

                    <textarea
                      placeholder="Image Detail / Description"
                      value={img.imageDetail}
                      onChange={(e) => {
                        const updated = [...detailData.days];
                        updated[dayIndex].images[index].imageDetail = e.target.value;
                        setDetailData({ ...detailData, days: updated });
                      }}
                    ></textarea>
                  </div>
                ))}
              </div>
            ))}

            <div className="modal-actions" style={{ marginTop: 12 }}>
              <button className="save-btn" onClick={handleSavePackageDetail}>
                âœ… Save
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailForm(null)}
              >
                âŒ Close
              </button>
            </div>

            <div style={{ marginTop: 20 }}>
              <h3>ğŸ“š Existing Image Details</h3>
              {existingDetails.length === 0 ? (
                <p style={{ color: "#666" }}>No details added yet.</p>
              ) : (
                <div style={{ display: "grid", gap: 10 }}>
                  {existingDetails.map((det) => (
                    <div
                      key={det._id}
                      style={{
                        border: "1px solid #ddd",
                        padding: "10px",
                        borderRadius: 10,
                      }}
                    >
                      <p><strong>ğŸ“… Day:</strong> {det.day}</p>
                      <p><strong>ğŸ“¸ Image:</strong> {det.imageName}</p>
                      <p><strong>â­ Rating:</strong> {det.rating}</p>
                      <p><strong>ğŸ’¬ Review:</strong> {det.review}</p>
                      <p><strong>ğŸ“„ Detail:</strong> {det.imageDetail}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
