// src/pages/Packages.jsx
import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [existingDetails, setExistingDetails] = useState([]);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // ðŸŒ Detail form fields (per-day fields match schema)
  const [detailData, setDetailData] = useState({
    mainImage: null, // optional cover; not stored in PackageDetail
    days: 1,
    dayDetails: [
      {
        day: "Day 1",
        touristPlace: "",
        state: "",
        rating: 4.5,
        image: null, // will become imageUrl on server
      },
    ],
  });

  // Disable scroll when any modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  // Initial fetch
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // Add or Update Package (base package)
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("âœ… Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  // Delete Package
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  // Edit base package
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  // View modal
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  // Reset add/edit modal
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  // Filter packages by tab
  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  // Detail form helpers
  // â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

  // Keep dayDetails array length in sync with "days" input
  const handleDaysChange = (e) => {
    const days = Math.max(parseInt(e.target.value || "1", 10), 1);
    const updated = [...detailData.dayDetails];

    while (updated.length < days) {
      const i = updated.length;
      updated.push({
        day: `Day ${i + 1}`,
        touristPlace: "",
        state: "",
        rating: 4.5,
        image: null,
      });
    }
    while (updated.length > days) {
      updated.pop();
    }

    setDetailData({ ...detailData, days, dayDetails: updated });
  };

  // Build days[] payload as the backend expects
  const buildDaysPayload = (detailData, pkg) =>
    detailData.dayDetails.map((d, i) => ({
      day: d.day?.trim() || `Day ${i + 1}`,
      touristPlace: d.touristPlace?.trim() || `Day ${i + 1}`,
      state: d.state?.trim() || pkg?.country || "Unknown",
      rating: Math.min(5, Math.max(1, Number(d.rating) || 4.5)),
      // imageUrl will be derived from uploaded file in same order
    }));

  // Collect images in same order as days[]
  const collectDayImages = (detailData) =>
    detailData.dayDetails.map((d) => d.image).filter(Boolean);

  // Save detail (bulk insert)
  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) {
      alert("Package ID missing! Please re-open the detail form.");
      return;
    }

    try {
      const daysPayload = buildDaysPayload(detailData, detailForm);
      if (!daysPayload.length) {
        alert("Please add at least one day.");
        return;
      }

      const images = collectDayImages(detailData);
      if (images.length !== daysPayload.length) {
        const proceed = window.confirm(
          "Some days are missing an image. Continue anyway?"
        );
        if (!proceed) return;
      }

      const formData = new FormData();
      formData.append("packageID", detailForm._id);              // schema field
      formData.append("days", JSON.stringify(daysPayload));      // bulk

      // Optional cover (ignored by controller if not used)
      if (detailData.mainImage) formData.append("image", detailData.mainImage);

      // One image per day (maps to imageUrl on server)
      images.forEach((file) => formData.append("images", file));

      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Failed to save details: " + (data.message || "Unknown error"));
        return;
      }

      alert("âœ… Package Detail saved successfully!");
      await loadExistingDetails(detailForm._id); // refresh preview
      // setDetailForm(null); // close if preferred
    } catch (error) {
      console.error("Error saving package detail:", error);
      alert("Something went wrong while saving the package detail.");
    }
  };

  // Open detail form and load existing details
  const handleOpenDetailForm = async (pkg) => {
    await loadExistingDetails(pkg._id);
    // Pre-fill default state with package.country if desired:
    setDetailData((prev) => ({
      ...prev,
      dayDetails: prev.dayDetails.map((d, i) => ({
        ...d,
        state: d.state || pkg.country || "",
      })),
    }));
    setDetailForm(pkg);
  };

  const loadExistingDetails = async (packageId) => {
    try {
      setExistingDetails([]);
      const res = await fetch(`${apiURL}/package-details/packagebyid/${packageId}`);
      if (res.ok) {
        const payload = await res.json();
        setExistingDetails(Array.isArray(payload.data) ? payload.data : []);
      } else {
        setExistingDetails([]);
      }
    } catch (e) {
      console.warn("Could not fetch existing package details", e);
      setExistingDetails([]);
    }
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>ðŸŒ Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          âž• Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>ðŸ·ï¸ {pkg.category?.title}</p>
                <p className="country-text">ðŸŒ {pkg.country}</p>
                <p>ðŸ’° â‚¹{pkg.price}</p>
                <p>â­ {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>ðŸ  Show on Home: {pkg.showOnHome ? "âœ… Yes" : "âŒ No"}</p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    âœ Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    âŒ Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    ðŸ‘ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              {/* Show on Home */}
              <div className="radio-group" style={{ gridColumn: "span 2" }}>
                <label>Show on Home:</label>
                <label>
