                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === false}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: false })
                    }
                  />
                  No
                </label>
              </div>

              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
