            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "âœ… Update" : "âž• Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                âŒ Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>ðŸ‘ View Package</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> â‚¹{viewPackage.price}</p>
            <p><strong>Rating:</strong> â­ {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "âœ… Yes" : "âŒ No"}
            </p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  handleOpenDetailForm(viewPackage);
                }}
              >
                ðŸ“‹ Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                âŒ Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Form */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{ maxWidth: "900px", maxHeight: "85vh", overflowY: "auto" }}
          >
            <h2>ðŸ“‹ Package Detail Form</h2>

            <h3>ðŸ–¼ï¸ Main Image</h3>
            <input
              type="file"
              accept="image/*"
              onChange={(e) =>
                setDetailData({ ...detailData, mainImage: e.target.files[0] })
              }
            />

            <h3>ðŸ“… Number of Days</h3>
            <input
              type="number"
              min="1"
              placeholder="Number of Days"
              value={detailData.days}
              onChange={handleDaysChange}
            />

            {/* Dynamic Days (all schema fields) */}
            {detailData.dayDetails.map((d, index) => (
              <div
                key={index}
                style={{
                  gridColumn: "span 2",
                  border: "1px solid #ccc",
                  borderRadius: "10px",
                  padding: "12px",
                  marginBottom: "12px",
                }}
              >
                <h3 style={{ marginBottom: "10px" }}>ðŸ—“ï¸ Day {index + 1} Section</h3>

                <input
                  type="text"
                  placeholder="Day label (e.g., Day 1)"
                  value={d.day}
                  onChange={(e) => {
                    const updated = [...detailData.dayDetails];
                    updated[index].day = e.target.value;
                    setDetailData({ ...detailData, dayDetails: updated });
                  }}
                  style={{ marginBottom: 8, width: "100%" }}
                />

                <input
                  type="text"
                  placeholder="Tourist Place"
                  value={d.touristPlace}
                  onChange={(e) => {
                    const updated = [...detailData.dayDetails];
                    updated[index].touristPlace = e.target.value;
                    setDetailData({ ...detailData, dayDetails: updated });
                  }}
                  style={{ marginBottom: 8, width: "100%" }}
                />

                <input
                  type="text"
                  placeholder="State (optional, uses package country if blank)"
                  value={d.state}
                  onChange={(e) => {
                    const updated = [...detailData.dayDetails];
                    updated[index].state = e.target.value;
                    setDetailData({ ...detailData, dayDetails: updated });
                  }}
                  style={{ marginBottom: 8, width: "100%" }}
                />

                <input
                  type="number"
                  min="1"
                  max="5"
                  step="0.1"
                  placeholder="Rating (1â€“5)"
                  value={d.rating}
                  onChange={(e) => {
                    const updated = [...detailData.dayDetails];
                    updated[index].rating = e.target.value;
                    setDetailData({ ...detailData, dayDetails: updated });
                  }}
                  style={{ marginBottom: 8, width: "100%" }}
                />

                <label style={{ display: "block", marginBottom: 6, fontWeight: 600 }}>
                  ðŸ–¼ï¸ Image (used as imageUrl)
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => {
                    const file = e.target.files?.[0] || null;
                    const updated = [...detailData.dayDetails];
                    updated[index].image = file;
                    setDetailData({ ...detailData, dayDetails: updated });
                  }}
                />
                {d.image && (
                  <div style={{ marginTop: 8, fontSize: 12, color: "#555" }}>
                    Selected: {d.image.name}
                  </div>
                )}
              </div>
            ))}

            <div className="modal-actions" style={{ marginTop: 12 }}>
              <button className="save-btn" onClick={handleSavePackageDetail}>
                âœ… Save
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailForm(null)}
              >
                âŒ Close
              </button>
            </div>

            {/* Existing details preview */}
            <div style={{ marginTop: 20 }}>
              <h3>ðŸ“š Existing Day Details</h3>
              {existingDetails.length === 0 ? (
                <p style={{ color: "#666" }}>No details added yet.</p>
              ) : (
                <div
                  style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
                    gap: "12px",
                  }}
                >
                  {existingDetails.map((d) => (
                    <div
                      key={d._id}
                      style={{
                        border: "1px solid #ddd",
                        borderRadius: 10,
                        padding: 10,
                      }}
                    >
                      <div style={{ fontWeight: 600, marginBottom: 6 }}>
                        {d.day || "Day"}
                      </div>
                      {d.imageUrl && (
                        <img
                          src={d.imageUrl}
                          alt={d.touristPlace}
                          style={{
                            width: "100%",
                            height: 120,
                            objectFit: "cover",
                            borderRadius: 8,
                            marginBottom: 6,
                          }}
                        />
                      )}
                      <div>ðŸ“ {d.touristPlace}</div>
                      <div>ðŸ—ºï¸ {d.state}</div>
                      <div>â­ {d.rating}</div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
