import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [detailsFormOpen, setDetailsFormOpen] = useState(false);
  const [selectedPackage, setSelectedPackage] = useState(null);

  // package form
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [mainImage, setMainImage] = useState(null);

  // details form
  const [numDays, setNumDays] = useState(0);
  const [daysData, setDaysData] = useState([]);

  useEffect(() => {
    fetchPackages();
  }, [activeTab]);

  const fetchPackages = async () => {
    try {
      const res = await fetch(`${apiURL}/package?type=${activeTab}`);
      const data = await res.json();
      setPackages(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handlePackageSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("price", price);
    formData.append("type", activeTab);
    if (mainImage) formData.append("mainImage", mainImage);

    const res = await fetch(`${apiURL}/package`, {
      method: "POST",
      body: formData,
    });
    if (res.ok) {
      alert("Package Created!");
      setFormOpen(false);
      fetchPackages();
    }
  };

  const openDetailsForm = (pkg) => {
    setSelectedPackage(pkg);
    setDetailsFormOpen(true);
    setNumDays(0);
    setDaysData([]);
  };

  // üßÆ when number of days changes
  const handleNumDaysChange = (val) => {
    const num = parseInt(val) || 0;
    setNumDays(num);
    const newData = Array.from({ length: num }, (_, i) => ({
      day: i + 1,
      images: [],
    }));
    setDaysData(newData);
  };

  // üñºÔ∏è add image entry inside a day
  const handleAddImage = (dayIndex) => {
    const updated = [...daysData];
    updated[dayIndex].images.push({
      file: null,
      imageName: "",
      rating: "",
      review: "",
      imageDetail: "",
    });
    setDaysData(updated);
  };

  // üîß update image fields
  const handleImageChange = (dayIndex, imgIndex, field, value) => {
    const updated = [...daysData];
    updated[dayIndex].images[imgIndex][field] = value;
    setDaysData(updated);
  };

  const handleFileChange = (dayIndex, imgIndex, file) => {
    const updated = [...daysData];
    updated[dayIndex].images[imgIndex].file = file;
    setDaysData(updated);
  };

  // üíæ submit all day-wise images
  const handleDetailsSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("packageId", selectedPackage._id);
    formData.append("daysData", JSON.stringify(daysData.map(d => ({
      day: d.day,
      images: d.images.map(i => ({
        imageName: i.imageName,
        rating: i.rating,
        review: i.review,
        imageDetail: i.imageDetail,
      })),
    }))));

    // append files
    daysData.forEach((day) =>
      day.images.forEach((img) => {
        if (img.file) formData.append("images", img.file);
      })
    );

    const res = await fetch(`${apiURL}/packageDetails`, {
      method: "POST",
      body: formData,
    });

    if (res.ok) {
      alert("Details Saved!");
      setDetailsFormOpen(false);
    } else {
      alert("Failed to save details");
    }
  };

  return (
    <div className="packages-container">
      <div className="tab-buttons">
        <button
          onClick={() => setActiveTab("Domestic")}
          className={activeTab === "Domestic" ? "active" : ""}
        >
          Domestic
        </button>
        <button
          onClick={() => setActiveTab("Overseas")}
          className={activeTab === "Overseas" ? "active" : ""}
        >
          Overseas
        </button>
      </div>

      <button
        onClick={() => setFormOpen(true)}
        style={{
          background: "#007bff",
          color: "white",
          border: "none",
          padding: "10px 20px",
          borderRadius: "10px",
          cursor: "pointer",
        }}
      >
        + Add Package
      </button>

      {/* Packages List */}
      <div className="packages-list">
        {packages.map((pkg) => (
          <div key={pkg._id} className="package-card">
            <img src={pkg.mainImageUrl} alt={pkg.title} />
            <h3>{pkg.title}</h3>
            <p>‚Çπ{pkg.price}</p>
            <button
              className="add-details-btn"
              onClick={() => openDetailsForm(pkg)}
            >
              Add Details
            </button>
          </div>
        ))}
      </div>

      {/* Create Package Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Add Package</h2>
            <form onSubmit={handlePackageSubmit}>
              <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
              <input
                type="number"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
              <label>Main Image:</label>
              <input
                type="file"
                onChange={(e) => setMainImage(e.target.files[0])}
              />
              <div className="modal-actions">
                <button type="submit" className="save-btn">
                  Save
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setFormOpen(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Package Details Modal */}
      {detailsFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Add Details for {selectedPackage?.title}</h2>

            <label>Number of Days:</label>
            <input
              type="number"
              value={numDays}
              onChange={(e) => handleNumDaysChange(e.target.value)}
              placeholder="Enter number of days"
              min="1"
            />

            {numDays > 0 &&
              daysData.map((day, dayIndex) => (
                <div key={dayIndex} className="day-section">
                  <h3>üóìÔ∏è Day {day.day}</h3>
                  <button
                    type="button"
                    className="add-image-btn"
                    onClick={() => handleAddImage(dayIndex)}
                  >
                    + Add Image
                  </button>

                  {day.images.map((img, imgIndex) => (
                    <div key={imgIndex} className="image-box">
                      <input
                        type="file"
                        onChange={(e) =>
                          handleFileChange(dayIndex, imgIndex, e.target.files[0])
                        }
                      />
                      <input
                        type="text"
                        placeholder="Image Name"
                        value={img.imageName}
                        onChange={(e) =>
                          handleImageChange(
                            dayIndex,
                            imgIndex,
                            "imageName",
                            e.target.value
                          )
                        }
                      />
                      <input
                        type="number"
                        placeholder="Rating"
                        value={img.rating}
                        onChange={(e) =>
                          handleImageChange(
                            dayIndex,
                            imgIndex,
                            "rating",
                            e.target.value
                          )
                        }
                      />
                      <input
                        type="text"
                        placeholder="Review"
                        value={img.review}
                        onChange={(e) =>
                          handleImageChange(
                            dayIndex,
                            imgIndex,
                            "review",
                            e.target.value
                          )
                        }
                      />
                      <textarea
                        placeholder="Image Detail"
                        value={img.imageDetail}
                        onChange={(e) =>
                          handleImageChange(
                            dayIndex,
                            imgIndex,
                            "imageDetail",
                            e.target.value
                          )
                        }
                      />
                    </div>
                  ))}
                </div>
              ))}

            <div className="modal-actions">
              <button onClick={handleDetailsSubmit} className="save-btn">
                Save Details
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailsFormOpen(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
