import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // ğŸŒ Detail form fields
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: 1,
    dayDetails: [
      {
        images: [],
        imageDetailsList: [],
      },
    ],
  });

  /* Disable scroll when modal open */
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  /* Fetch Data */
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  /* Add or Update Package */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("âœ… Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* Delete Package */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* Edit */
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* View */
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  /* Reset */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  /* Filter packages */
  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  // ğŸ§© Detail form logic
  const handleDaysChange = (e) => {
    const days = parseInt(e.target.value);
    const updatedDetails = [...detailData.dayDetails];

    while (updatedDetails.length < days) {
      updatedDetails.push({ images: [], imageDetailsList: [] });
    }
    while (updatedDetails.length > days) {
      updatedDetails.pop();
    }

    setDetailData({ ...detailData, days, dayDetails: updatedDetails });
  };

  // âœ… SAVE PACKAGE DETAIL FORM TO BACKEND
  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) {
      alert("Package ID missing! Please re-open the detail form.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("packageId", detailForm._id);
      formData.append("days", detailData.days);

      if (detailData.mainImage) {
        formData.append("mainImage", detailData.mainImage);
      }

      // Add each day's data
      detailData.dayDetails.forEach((day, index) => {
        day.images.forEach((file, imgIndex) => {
          formData.append(`dayDetails[${index}][images]`, file);
          const imgDetails = day.imageDetailsList?.[imgIndex] || {};
          formData.append(
            `dayDetails[${index}][imageDetailsList][${imgIndex}][name]`,
            imgDetails.name || ""
          );
          formData.append(
            `dayDetails[${index}][imageDetailsList][${imgIndex}][rating]`,
            imgDetails.rating || ""
          );
          formData.append(
            `dayDetails[${index}][imageDetailsList][${imgIndex}][review]`,
            imgDetails.review || ""
          );
          formData.append(
            `dayDetails[${index}][imageDetailsList][${imgIndex}][details]`,
            imgDetails.details || ""
          );
        });
      });

      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        alert("âŒ Failed to save details: " + (data.message || ""));
        return;
      }

      alert("âœ… Package Detail saved successfully!");
      setDetailForm(null);
    } catch (error) {
      console.error("Error saving package detail:", error);
      alert("Something went wrong while saving the package detail.");
    }
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>ğŸŒ Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          â• Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>ğŸ·ï¸ {pkg.category?.title}</p>
                <p className="country-text">ğŸŒ {pkg.country}</p>
                <p>ğŸ’° â‚¹{pkg.price}</p>
                <p>â­ {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>ğŸ  Show on Home: {pkg.showOnHome ? "âœ… Yes" : "âŒ No"}</p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    âœ Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    âŒ Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    ğŸ‘ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              {/* Show on Home */}
              <div className="radio-group" style={{ gridColumn: "span 2" }}>
                <label>Show on Home:</label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === true}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: true })
                    }
                  />
                  Yes
                </label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === false}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: false })
                    }
                  />
                  No
                </label>
              </div>

              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />
              <select
                value={newPackage.type}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, type: e.target.value })
                }
              >
                <option value="">Select Type</option>
                <option value="Domestic">Domestic</option>
                <option value="Overseas">Overseas</option>
              </select>
              <input
                type="text"
                placeholder="Country"
                value={newPackage.country}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, country: e.target.value })
                }
              />
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                <option value="">Select Subcategory</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.title}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    rating: parseFloat(e.target.value),
                  })
                }
              />
              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, review: e.target.value })
                }
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files[0])}
              />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "âœ… Update" : "â• Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                âŒ Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>ğŸ‘ View Package</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> â‚¹{viewPackage.price}</p>
            <p><strong>Rating:</strong> â­ {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "âœ… Yes" : "âŒ No"}
            </p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  setDetailForm(viewPackage);
                }}
              >
                ğŸ“‹ Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                âŒ Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Detail Form */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{ maxWidth: "700px", maxHeight: "80vh", overflowY: "auto" }}
          >
            <h2>ğŸ“‹ Package Detail Form</h2>

            <h3>ğŸ–¼ï¸ Main Image</h3>
            <input
              type="file"
              accept="image/*"
              onChange={(e) =>
                setDetailData({ ...detailData, mainImage: e.target.files[0] })
              }
            />

            <h3>ğŸ“… Number of Days</h3>
            <input
              type="number"
              min="1"
              placeholder="Number of Days"
              value={detailData.days}
              onChange={handleDaysChange}
            />

            {/* Dynamic Days */}
            {detailData.dayDetails.map((day, index) => (
              <div
                key={index}
                style={{
                  gridColumn: "span 2",
                  border: "1px solid #ccc",
                  borderRadius: "10px",
                  padding: "10px",
                  marginBottom: "12px",
                }}
              >
                <h3 style={{ marginBottom: "10px" }}>
                  ğŸ—“ï¸ Day {index + 1} Section
                </h3>

                {/* Multiple Images */}
                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => {
                    const files = Array.from(e.target.files);
                    const updatedDetails = [...detailData.dayDetails];
                    updatedDetails[index].images = files;
                    updatedDetails[index].imageDetailsList = files.map(() => ({
                      name: "",
                      rating: "",
                      review: "",
                      details: "",
                    }));
                    setDetailData({
                      ...detailData,
                      dayDetails: updatedDetails,
                    });
                  }}
                />

                {/* Each image form */}
                {day.images && day.images.length > 0 && (
                  <div style={{ marginTop: "10px" }}>
                    {day.images.map((img, imgIndex) => (
                      <div
                        key={imgIndex}
                        style={{
                          display: "grid",
                          gridTemplateColumns: "repeat(2, 1fr)",
                          gap: "6px",
                          border: "1px solid #ddd",
                          padding: "8px",
                          borderRadius: "8px",
                          marginBottom: "8px",
                        }}
                      >
                        <p
                          style={{
                            gridColumn: "span 2",
                            fontWeight: "bold",
                            marginBottom: "4px",
                          }}
                        >
                          ğŸ–¼ï¸ Image {imgIndex + 1}: {img.name}
                        </p>
                        <input
                          type="text"
                          placeholder="Image Name"
                          value={day.imageDetailsList?.[imgIndex]?.name || ""}
                          onChange={(e) => {
                            const updated = [...detailData.dayDetails];
                            updated[index].imageDetailsList[imgIndex].name =
                              e.target.value;
                            setDetailData({
                              ...detailData,
                              dayDetails: updated,
                            });
                          }}
                        />
                        <input
                          type="number"
                          placeholder="Rating"
                          value={day.imageDetailsList?.[imgIndex]?.rating || ""}
                          onChange={(e) => {
                            const updated = [...detailData.dayDetails];
                            updated[index].imageDetailsList[imgIndex].rating =
                              e.target.value;
                            setDetailData({
                              ...detailData,
                              dayDetails: updated,
                            });
                          }}
                        />
                        <input
                          type="number"
                          placeholder="Review"
                          value={day.imageDetailsList?.[imgIndex]?.review || ""}
                          onChange={(e) => {
                            const updated = [...detailData.dayDetails];
                            updated[index].imageDetailsList[imgIndex].review =
                              e.target.value;
                            setDetailData({
                              ...detailData,
                              dayDetails: updated,
                            });
                          }}
                        />
                        <textarea
                          placeholder="Image Details"
                          style={{ gridColumn: "span 2" }}
                          value={day.imageDetailsList?.[imgIndex]?.details || ""}
                          onChange={(e) => {
                            const updated = [...detailData.dayDetails];
                            updated[index].imageDetailsList[imgIndex].details =
                              e.target.value;
                            setDetailData({
                              ...detailData,
                              dayDetails: updated,
                            });
                          }}
                        />
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}

            <div className="modal-actions">
              <button className="save-btn" onClick={handleSavePackageDetail}>
                âœ… Save
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailForm(null)}
              >
                âŒ Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
