import React from "react";
import { Navigate } from "react-router-dom";

const isAdmin = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user?.role === "admin"; // adjust according to your auth
};

const AdminRoute = ({ children }) => {
  if (!isAdmin()) {
    return <Navigate to="/admin/not-authorized" replace />; // redirect non-admins
  }
  return children; // render valid JSX (layout)
};

export default AdminRoute;
