import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  // üßæ Detail Modal State
  const [detailFormOpen, setDetailFormOpen] = useState(false);
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: 1,
    dayWiseImages: [
      {
        dayNumber: 1,
        images: [],
        imageNames: [],
        imageDetails: [],
        ratings: [],
        reviews: [],
      },
    ],
  });

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    country: "",
    showOnHome: false,
  });

  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailFormOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailFormOpen]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const res = await fetch(`${apiURL}/packages`);
      const data = await res.json();
      setPackages(data.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await fetch(`${apiURL}/categories`);
      const data = await res.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const handleOpenDetailForm = () => {
    setDetailFormOpen(true);
    setDetailData({
      mainImage: null,
      days: 1,
      dayWiseImages: [
        {
          dayNumber: 1,
          images: [],
          imageNames: [],
          imageDetails: [],
          ratings: [],
          reviews: [],
        },
      ],
    });
  };

  const handleDaysChange = (days) => {
    const num = parseInt(days);
    const updated = [];
    for (let i = 1; i <= num; i++) {
      updated.push({
        dayNumber: i,
        images: [],
        imageNames: [],
        imageDetails: [],
        ratings: [],
        reviews: [],
      });
    }
    setDetailData({ ...detailData, days: num, dayWiseImages: updated });
  };

  const handleSaveDetails = async () => {
    const formData = new FormData();
    formData.append("days", detailData.days);
    if (detailData.mainImage) formData.append("mainImage", detailData.mainImage);

    detailData.dayWiseImages.forEach((day, idx) => {
      formData.append(`dayWiseImages[${idx}][dayNumber]`, day.dayNumber);
      day.images.forEach((img) =>
        formData.append(`dayWiseImages[${idx}][images]`, img)
      );
      day.imageNames.forEach((n) =>
        formData.append(`dayWiseImages[${idx}][imageNames]`, n)
      );
      day.imageDetails.forEach((d) =>
        formData.append(`dayWiseImages[${idx}][imageDetails]`, d)
      );
      day.ratings.forEach((r) =>
        formData.append(`dayWiseImages[${idx}][ratings]`, r)
      );
      day.reviews.forEach((r) =>
        formData.append(`dayWiseImages[${idx}][reviews]`, r)
      );
    });

    try {
      const res = await fetch(`${apiURL}/packages/details/${viewPackage._id}`, {
        method: "PUT",
        body: formData,
      });
      if (!res.ok) throw new Error("Failed to save details");
      alert("‚úÖ Details saved successfully!");
      setDetailFormOpen(false);
      fetchPackages();
    } catch (err) {
      console.error(err);
      alert("Error saving details.");
    }
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="package-grid">
        {packages.map((pkg) => (
          <div key={pkg._id} className="package-card">
            {pkg.image && (
              <img src={pkg.image} alt={pkg.title} className="package-img" />
            )}
            <div className="package-info">
              <h3>{pkg.title}</h3>
              <p>üí∞ ‚Çπ{pkg.price}</p>
              <button
                className="view-btn"
                onClick={() => {
                  setViewPackage(pkg);
                  handleOpenDetailForm();
                }}
              >
                üßæ Details
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* üß© Detail Form Modal */}
      {detailFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>üìã Package Details</h2>

            <div
              className="form-grid"
              style={{
                maxHeight: "70vh",
                overflowY: "auto",
                paddingRight: "10px",
              }}
            >
              {/* Main Image */}
              <label>Main Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  setDetailData({ ...detailData, mainImage: e.target.files[0] })
                }
              />

              {/* Number of Days */}
              <label>Number of Days:</label>
              <input
                type="number"
                min="1"
                value={detailData.days}
                onChange={(e) => handleDaysChange(e.target.value)}
              />

              {detailData.dayWiseImages.map((day, dayIdx) => (
                <div
                  key={dayIdx}
                  className="day-section"
                  style={{
                    background: "#f9f9f9",
                    padding: "10px",
                    borderRadius: "8px",
                    marginBottom: "15px",
                  }}
                >
                  <h4>üìÖ Day {day.dayNumber}</h4>

                  {/* Upload multiple images */}
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) => {
                      const files = Array.from(e.target.files);
                      const updated = [...detailData.dayWiseImages];
                      updated[dayIdx].images = files;
                      updated[dayIdx].imageNames = files.map(() => "");
                      updated[dayIdx].imageDetails = files.map(() => "");
                      updated[dayIdx].ratings = files.map(() => "");
                      updated[dayIdx].reviews = files.map(() => "");
                      setDetailData({ ...detailData, dayWiseImages: updated });
                    }}
                  />

                  {/* Multi-field for each image */}
                  {day.images.length > 0 && (
                    <div
                      style={{
                        display: "flex",
                        flexDirection: "column",
                        gap: "10px",
                        marginTop: "10px",
                      }}
                    >
                      {day.images.map((file, imgIdx) => (
                        <div
                          key={imgIdx}
                          style={{
                            background: "#fff",
                            padding: "10px",
                            borderRadius: "8px",
                            boxShadow: "0 1px 4px rgba(0,0,0,0.1)",
                          }}
                        >
                          <label style={{ fontWeight: "bold" }}>
                            üñºÔ∏è {file.name.slice(0, 25)}
                          </label>

                          <div
                            style={{
                              display: "grid",
                              gridTemplateColumns:
                                "repeat(auto-fit, minmax(120px, 1fr))",
                              gap: "8px",
                              marginTop: "6px",
                            }}
                          >
                            <input
                              type="text"
                              placeholder="Image Name"
                              value={day.imageNames[imgIdx] || ""}
                              onChange={(e) => {
                                const updated = [...detailData.dayWiseImages];
                                updated[dayIdx].imageNames[imgIdx] =
                                  e.target.value;
                                setDetailData({
                                  ...detailData,
                                  dayWiseImages: updated,
                                });
                              }}
                            />
                            <input
                              type="text"
                              placeholder="Image Detail"
                              value={day.imageDetails[imgIdx] || ""}
                              onChange={(e) => {
                                const updated = [...detailData.dayWiseImages];
                                updated[dayIdx].imageDetails[imgIdx] =
                                  e.target.value;
                                setDetailData({
                                  ...detailData,
                                  dayWiseImages: updated,
                                });
                              }}
                            />
                            <input
                              type="number"
                              placeholder="Rating"
                              min="0"
                              max="5"
                              step="0.1"
                              value={day.ratings[imgIdx] || ""}
                              onChange={(e) => {
                                const updated = [...detailData.dayWiseImages];
                                updated[dayIdx].ratings[imgIdx] =
                                  e.target.value;
                                setDetailData({
                                  ...detailData,
                                  dayWiseImages: updated,
                                });
                              }}
                            />
                            <input
                              type="number"
                              placeholder="Review"
                              value={day.reviews[imgIdx] || ""}
                              onChange={(e) => {
                                const updated = [...detailData.dayWiseImages];
                                updated[dayIdx].reviews[imgIdx] =
                                  e.target.value;
                                setDetailData({
                                  ...detailData,
                                  dayWiseImages: updated,
                                });
                              }}
                            />
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>

            <div className="modal-actions">
              <button onClick={handleSaveDetails} className="save-btn">
                üíæ Save Details
              </button>
              <button
                onClick={() => setDetailFormOpen(false)}
                className="cancel-btn"
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
