import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [categories, setCategories] = useState([]); // üÜï dynamic categories
  const [activeTab, setActiveTab] = useState("");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
  });

  /* Disable scroll when modal open */
  useEffect(() => {
    document.body.style.overflow = formOpen || viewPackage ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage]);

  /* Fetch Data */
  useEffect(() => {
    fetchCategories();
    fetchPackages();
  }, []);

  /* üÜï Fetch categories dynamically */
  const fetchCategories = async () => {
    try {
      const res = await fetch(`${apiURL}/categories`);
      const data = await res.json();
      if (data?.data?.length) {
        setCategories(data.data);
        setActiveTab(data.data[0].name); // default to first category
        setNewPackage((prev) => ({ ...prev, category: data.data[0].name }));
      } else {
        console.warn("No categories found");
      }
    } catch (err) {
      console.error("Error fetching categories:", err);
    }
  };

  /* Fetch Packages */
  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
      setPackages([]);
    }
  };

  /* Add or Update */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.price) {
      alert("Please fill Title and Price fields!");
      return;
    }

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* Delete */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* Edit */
  const handleEditPackage = (pkg) => {
    setNewPackage({ ...pkg });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* View */
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  /* Reset */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      category: categories[0]?.name || "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
    });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* üÜï Dynamic Tabs */}
      <div className="tabs">
        {categories.length > 0 ? (
          categories.map((cat) => (
            <button
              key={cat._id || cat.name}
              className={`tab-btn ${activeTab === cat.name ? "active" : ""}`}
              onClick={() => setActiveTab(cat.name)}
            >
              {cat.name}
            </button>
          ))
        ) : (
          <p className="empty-text">Loading categories...</p>
        )}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {packages.length > 0 ? (
          packages
            .filter((pkg) => pkg.category === activeTab)
            .map((pkg) => (
              <div key={pkg._id} className="package-card">
                {pkg.image && (
                  <img src={pkg.image} alt={pkg.title} className="package-img" />
                )}
                <div className="package-info">
                  <h3>{pkg.title}</h3>
                  {pkg.country && <p className="country-text">{pkg.country}</p>}
                  <p>
                    üí∞{" "}
                    {pkg.price?.toString().startsWith("‚Çπ")
                      ? pkg.price
                      : `‚Çπ${pkg.price}`}
                  </p>
                  <p>‚≠ê {pkg.rating}</p>
                  <p>Review: {pkg.review}</p>
                  <div className="card-actions">
                    <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>
                      ‚úè Edit
                    </button>
                    <button
                      className="remove-btn"
                      onClick={() => handleRemovePackage(pkg._id)}
                    >
                      ‚ùå Remove
                    </button>
                    <button className="view-btn" onClick={() => handleViewDetails(pkg)}>
                      üëÅ View
                    </button>
                  </div>
                </div>
              </div>
            ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid" style={{ maxHeight: "70vh", overflowY: "auto" }}>
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) => setNewPackage({ ...newPackage, title: e.target.value })}
              />

              {/* üÜï Dynamic Category Dropdown */}
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                {categories.map((cat) => (
                  <option key={cat._id || cat.name} value={cat.name}>
                    {cat.name}
                  </option>
                ))}
              </select>

              {/* Optional Country Field */}
              <input
                type="text"
                placeholder="Country (optional)"
                value={newPackage.country}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, country: e.target.value })
                }
              />

              <input
                type="text"
                placeholder="Price (‚Çπ25,000 per person)"
                value={newPackage.price}
                onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })}
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, rating: parseFloat(e.target.value) })
                }
              />
              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) => setNewPackage({ ...newPackage, review: e.target.value })}
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files[0])}
              />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Details Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{viewPackage.title}</h2>
            {viewPackage.image && (
              <img src={viewPackage.image} alt={viewPackage.title} className="package-img" />
            )}
            <p>
              <strong>Category:</strong> {viewPackage.category}
            </p>
            {viewPackage.country && (
              <p>
                <strong>Country:</strong> {viewPackage.country}
              </p>
            )}
            <p>
              <strong>Price:</strong> ‚Çπ{viewPackage.price}
            </p>
            <p>
              <strong>Rating:</strong> {viewPackage.rating}
            </p>
            <p>
              <strong>Review:</strong> {viewPackage.review}
            </p>
            <div className="modal-actions">
              <button onClick={closeViewDetails} className="cancel-btn">
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
