import React, { useState, useEffect } from "react";
import "./Packages.css";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    description: "",
    destination: "",
    category: "Domestic",
    duration: "",
    price: "",
    inclusions: "",
    exclusions: "",
    availability: "",
    rating: "",
    status: "Active",
    state: "",
    touristPlace: "",
    image: "",
  });

  /* -------------------------- FETCH PACKAGES -------------------------- */
  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/packages");
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
      setPackages([]);
    }
  };

  const filteredPackages = Array.isArray(packages)
    ? packages.filter(
        (pkg) =>
          pkg.category?.toLowerCase() === activeTab.toLowerCase()
      )
    : [];

  /* ------------------------ ADD / UPDATE PACKAGE ------------------------ */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.price || !newPackage.destination) {
      alert("Please fill Title, Destination, and Price fields!");
      return;
    }

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `http://localhost:5000/api/packages/${editingId}`
        : "http://localhost:5000/api/packages";

      // ‚úÖ Build form data (to support image upload)
      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("imageFile", imageFile);

      console.log("üì§ Sending:", newPackage);

      const response = await fetch(url, {
        method,
        body: formData, // ‚úÖ send form-data instead of JSON
      });

      const data = await response.json();
      console.log("üì• Response:", data);

      if (!response.ok) {
        console.error("‚ùå Server Error:", data);
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* ----------------------------- DELETE ----------------------------- */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;

    try {
      await fetch(`http://localhost:5000/api/packages/${id}`, {
        method: "DELETE",
      });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* ----------------------------- EDIT ----------------------------- */
  const handleEditPackage = (pkg) => {
    setNewPackage({ ...pkg });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* ----------------------------- RESET ----------------------------- */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      description: "",
      destination: "",
      category: "Domestic",
      duration: "",
      price: "",
      inclusions: "",
      exclusions: "",
      availability: "",
      rating: "",
      status: "Active",
      state: "",
      touristPlace: "",
      image: "",
    });
  };

  /* ----------------------------- UI ----------------------------- */
  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "International"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Cards */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p className="desc">{pkg.description}</p>
                <p>
                  <strong>üìç {pkg.destination}</strong>
                </p>
                <p>
                  <strong>üí∞ {pkg.price}</strong> ‚Ä¢ {pkg.duration}
                </p>
                <p>
                  <strong>‚≠ê</strong> {pkg.rating}
                </p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    ‚úèÔ∏è Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    ‚ùå Remove
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">
            No packages available in this category.
          </p>
        )}
      </div>

      {/* Modal Form */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid">
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />
              <textarea
                placeholder="Description"
                value={newPackage.description}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, description: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Destination"
                value={newPackage.destination}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, destination: e.target.value })
                }
              />
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                <option value="Domestic">Domestic</option>
                <option value="International">International</option>
              </select>
              <input
                type="text"
                placeholder="Duration"
                value={newPackage.duration}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, duration: e.target.value })
                }
              />
              <input
                type="number"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Inclusions"
                value={newPackage.inclusions}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, inclusions: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Exclusions"
                value={newPackage.exclusions}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, exclusions: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Availability"
                value={newPackage.availability}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    availability: e.target.value,
                  })
                }
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    rating: parseFloat(e.target.value),
                  })
                }
              />
              <select
                value={newPackage.status}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, status: e.target.value })
                }
              >
                <option value="Active">Active</option>
                <option value="Inactive">Inactive</option>
              </select>
              <input
                type="text"
                placeholder="State"
                value={newPackage.state}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, state: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Tourist Place"
                value={newPackage.touristPlace}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    touristPlace: e.target.value,
                  })
                }
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    setImageFile(file);
                    setNewPackage({
                      ...newPackage,
                      image: URL.createObjectURL(file),
                    });
                  }
                }}
              />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
