import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  const [detailsData, setDetailsData] = useState({
    mainImage: null,
    days: 1,
    dayImages: [],
  });

  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) formData.append(key, newPackage[key]);
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  const filteredPackages = packages.filter((pkg) => pkg.type === activeTab);

  const handleDetailChange = (key, value) => {
    setDetailsData((prev) => ({ ...prev, [key]: value }));
  };

  const handleDayImageChange = (dayIndex, files) => {
    const newImages = Array.from(files).map((file) => ({
      file,
      name: "",
      rating: 0,
      review: "",
      details: "",
    }));

    setDetailsData((prev) => {
      const updated = [...prev.dayImages];
      updated[dayIndex] = { day: dayIndex + 1, images: newImages };
      return { ...prev, dayImages: updated };
    });
  };

  const handleSaveDetails = async () => {
    if (!detailsData.mainImage) return alert("Please upload main image!");

    try {
      const formData = new FormData();
      formData.append("mainImage", detailsData.mainImage);
      formData.append("days", detailsData.days);

      detailsData.dayImages.forEach((day, index) => {
        day.images.forEach((img, i) => {
          formData.append(`dayImages[${index}][${i}]`, img.file);
          formData.append(`dayImages[${index}][${i}][name]`, img.name);
          formData.append(`dayImages[${index}][${i}][rating]`, img.rating);
          formData.append(`dayImages[${index}][${i}][review]`, img.review);
          formData.append(`dayImages[${index}][${i}][details]`, img.details);
        });
      });

      const response = await fetch(`${apiURL}/packages/${detailForm._id}/details`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Failed to save details");

      alert("‚úÖ Package details saved successfully!");
      setDetailForm(null);
      setDetailsData({ mainImage: null, days: 1, dayImages: [] });
    } catch (error) {
      console.error("Error saving details:", error);
    }
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && <img src={pkg.image} alt={pkg.title} className="package-img" />}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>üè† {pkg.showOnHome ? "‚úÖ Show on Home" : "‚ùå Not on Home"}</p>
                <div className="card-actions">
                  <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>‚úè Edit</button>
                  <button className="remove-btn" onClick={() => handleRemovePackage(pkg._id)}>‚ùå Remove</button>
                  <button className="view-btn" onClick={() => handleViewDetails(pkg)}>üëÅ View</button>
                  <button className="edit-btn" onClick={() => setDetailForm(pkg)}>üìã Detail</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages found.</p>
        )}
      </div>

      {/* Detail Form Modal */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{
              width: "90%",
              maxWidth: "800px",
              maxHeight: "85vh",
              overflowY: "auto",
            }}
          >
            <h2>üñºÔ∏è Add Package Details</h2>

            <label>Main Image:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleDetailChange("mainImage", e.target.files[0])}
            />

            <label>Total Days:</label>
            <input
              type="number"
              min="1"
              value={detailsData.days}
              onChange={(e) => handleDetailChange("days", parseInt(e.target.value))}
            />

            {Array.from({ length: detailsData.days }).map((_, dayIndex) => (
              <div key={dayIndex} className="day-section">
                <h4>üóìÔ∏è Day {dayIndex + 1}</h4>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={(e) => handleDayImageChange(dayIndex, e.target.files)}
                />
                {detailsData.dayImages[dayIndex]?.images?.map((img, i) => (
                  <div key={i} className="image-box">
                    <input
                      type="text"
                      placeholder="Image Name"
                      value={img.name}
                      onChange={(e) => {
                        const updated = [...detailsData.dayImages];
                        updated[dayIndex].images[i].name = e.target.value;
                        setDetailsData({ ...detailsData, dayImages: updated });
                      }}
                    />
                    <input
                      type="number"
                      min="0"
                      max="5"
                      step="0.1"
                      placeholder="Rating"
                      value={img.rating}
                      onChange={(e) => {
                        const updated = [...detailsData.dayImages];
                        updated[dayIndex].images[i].rating = e.target.value;
                        setDetailsData({ ...detailsData, dayImages: updated });
                      }}
                    />
                    <input
                      type="number"
                      placeholder="Review (Number)"
                      value={img.review}
                      onChange={(e) => {
                        const updated = [...detailsData.dayImages];
                        updated[dayIndex].images[i].review = e.target.value;
                        setDetailsData({ ...detailsData, dayImages: updated });
                      }}
                    />
                    <textarea
                      placeholder="Image Details"
                      value={img.details}
                      onChange={(e) => {
                        const updated = [...detailsData.dayImages];
                        updated[dayIndex].images[i].details = e.target.value;
                        setDetailsData({ ...detailsData, dayImages: updated });
                      }}
                    />
                  </div>
                ))}
              </div>
            ))}

            <div className="modal-actions">
              <button onClick={handleSaveDetails} className="save-btn">
                üíæ Save Details
              </button>
              <button onClick={() => setDetailForm(null)} className="cancel-btn">
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
