import React, { useState } from 'react';
import './Packages.css';

export default function Packages() {
  const [activeTab, setActiveTab] = useState('Domestic');
  const [packages, setPackages] = useState([
    {
      id: Date.now() + 1,
      title: 'Dubai Luxury Tour',
      description: 'Experience luxury in Dubai with Burj Khalifa, Desert Safari, and shopping.',
      category: 'International',
      duration: '5N/6D',
      price: '‚Çπ1,20,000',
      inclusions: 'Flights, Hotels, Transfers, Sightseeing',
      exclusions: 'Personal expenses, Visa fees',
      availability: 'Available',
      rating: 4.8,
      status: 'Active',
      state: '',
      touristPlace: 'Dubai, Abu Dhabi',
      image: 'https://example.com/dubai.jpg'
    },
    {
      id: Date.now() + 2,
      title: 'Kerala Backwaters',
      description: 'Enjoy a peaceful houseboat ride and Ayurvedic therapies in Kerala.',
      category: 'Domestic',
      duration: '3N/4D',
      price: '‚Çπ25,000',
      inclusions: 'Hotels, Breakfast, Transfers',
      exclusions: 'Flights, Personal expenses',
      availability: 'Available',
      rating: 4.5,
      status: 'Active',
      state: 'Kerala',
      touristPlace: 'Alleppey, Kumarakom',
      image: 'https://example.com/kerala.jpg'
    },
  ]);

  const [newPackage, setNewPackage] = useState({
    title: '',
    description: '',
    category: 'Domestic',
    duration: '',
    price: '',
    inclusions: '',
    exclusions: '',
    availability: '',
    rating: '',
    status: 'Active',
    state: '',
    touristPlace: '',
    image: ''
  });

  const [editingId, setEditingId] = useState(null); // track which package is being edited

  const filteredPackages = packages.filter(pkg => pkg.category === activeTab);

  const handleAddOrUpdatePackage = () => {
    if (!newPackage.title || !newPackage.price || !newPackage.duration) return;

    if (editingId) {
      // update existing package
      setPackages(packages.map(pkg =>
        pkg.id === editingId ? { ...newPackage, id: editingId } : pkg
      ));
      setEditingId(null);
    } else {
      // add new package
      const id = Date.now();
      setPackages([...packages, { ...newPackage, id }]);
    }

    // reset form
    setNewPackage({
      title: '',
      description: '',
      category: 'Domestic',
      duration: '',
      price: '',
      inclusions: '',
      exclusions: '',
      availability: '',
      rating: '',
      status: 'Active',
      state: '',
      touristPlace: '',
      image: ''
    });
  };

  const handleRemovePackage = (id) => {
    if (window.confirm('Are you sure you want to remove this package?')) {
      setPackages(packages.filter(pkg => pkg.id !== id));
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage(pkg); // load package data into form
    setEditingId(pkg.id); // set current editing package
    setActiveTab(pkg.category); // switch to correct tab automatically
  };

  return (
    <div className="packages-container">
      <h1 className="page-title">üåç Travel Packages</h1>

      {/* Tabs */}
      <div className="tabs">
        {['Domestic', 'International'].map(tab => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package List */}
      <div className="package-list">
        {filteredPackages.map(pkg => (
          <div key={pkg.id} className="package-card">
            {pkg.image && <img src={pkg.image} alt={pkg.title} className="package-img" />}
            <h3>{pkg.title}</h3>
            <p className="desc">{pkg.description}</p>
            <p><strong>Price:</strong> {pkg.price}</p>
            <p><strong>Duration:</strong> {pkg.duration}</p>
            <p><strong>Inclusions:</strong> {pkg.inclusions}</p>
            <p><strong>Exclusions:</strong> {pkg.exclusions}</p>
            <p><strong>Availability:</strong> {pkg.availability}</p>
            <p><strong>Rating:</strong> ‚≠ê {pkg.rating}</p>
            <p><strong>Status:</strong> {pkg.status}</p>
            <p><strong>State:</strong> {pkg.state}</p>
            <p><strong>Tourist Place:</strong> {pkg.touristPlace}</p>

            <div className="actions">
              <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>‚úèÔ∏è Edit</button>
              <button className="remove-btn" onClick={() => handleRemovePackage(pkg.id)}>‚ùå Remove</button>
            </div>
          </div>
        ))}
      </div>

      {/* Add or Update Package */}
      <div className="add-package-form">
        <h2>{editingId ? 'Update Package' : 'Add New Package'}</h2>
        <div className="add-package-card">
          <input type="text" placeholder="Title"
            value={newPackage.title}
            onChange={e => setNewPackage({ ...newPackage, title: e.target.value })} />

          <textarea placeholder="Description"
            value={newPackage.description}
            onChange={e => setNewPackage({ ...newPackage, description: e.target.value })} />

          <select
            value={newPackage.category}
            onChange={e => setNewPackage({ ...newPackage, category: e.target.value })}
          >
            <option value="Domestic">Domestic</option>
            <option value="International">International</option>
          </select>

          <input type="text" placeholder="Duration"
            value={newPackage.duration}
            onChange={e => setNewPackage({ ...newPackage, duration: e.target.value })} />

          <input type="text" placeholder="Price"
            value={newPackage.price}
            onChange={e => setNewPackage({ ...newPackage, price: e.target.value })} />

          <input type="text" placeholder="Inclusions"
            value={newPackage.inclusions}
            onChange={e => setNewPackage({ ...newPackage, inclusions: e.target.value })} />

          <input type="text" placeholder="Exclusions"
            value={newPackage.exclusions}
            onChange={e => setNewPackage({ ...newPackage, exclusions: e.target.value })} />

          <input type="text" placeholder="Availability"
            value={newPackage.availability}
            onChange={e => setNewPackage({ ...newPackage, availability: e.target.value })} />

          <input type="number" placeholder="Rating"
            value={newPackage.rating}
            onChange={e => setNewPackage({ ...newPackage, rating: e.target.value })} />

          <select
            value={newPackage.status}
            onChange={e => setNewPackage({ ...newPackage, status: e.target.value })}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>

          <input type="text" placeholder="State"
            value={newPackage.state}
            onChange={e => setNewPackage({ ...newPackage, state: e.target.value })} />

          <input type="text" placeholder="Tourist Place"
            value={newPackage.touristPlace}
            onChange={e => setNewPackage({ ...newPackage, touristPlace: e.target.value })} />

          <input type="text" placeholder="Image URL"
            value={newPackage.image}
            onChange={e => setNewPackage({ ...newPackage, image: e.target.value })} />

          <button className="add-btn" onClick={handleAddOrUpdatePackage}>
            {editingId ? '‚úÖ Update Package' : '‚ûï Add Package'}
          </button>
        </div>
      </div>
    </div>
  );
}
