import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [detailsFormOpen, setDetailsFormOpen] = useState(false);
  const [viewPackage, setViewPackage] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    country: "",
    showOnHome: false,
  });

  const [detailsData, setDetailsData] = useState({
    mainImage: null,
    days: 1,
    dayImages: [],
  });

  // üåÄ Lock body scroll when modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailsFormOpen ? "hidden" : "auto";
  }, [formOpen, viewPackage, detailsFormOpen]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const res = await fetch(`${apiURL}/packages`);
      const data = await res.json();
      setPackages(data.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await fetch(`${apiURL}/categories`);
      const data = await res.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.country) {
      alert("Please fill all required fields!");
      return;
    }

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const res = await fetch(url, { method, body: formData });
      const data = await res.json();

      if (!res.ok) {
        alert("‚ùå Error saving package");
        return;
      }

      alert("‚úÖ Package saved successfully!");
      resetForm();
      fetchPackages();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Delete this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages((prev) => prev.filter((p) => p._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      country: "",
      showOnHome: false,
    });
  };

  const openDetailsForm = (pkg) => {
    setDetailsFormOpen(true);
    setEditingId(pkg._id);
    setDetailsData({
      mainImage: null,
      days: pkg.days || 1,
      dayImages: Array(pkg.days || 1)
        .fill(null)
        .map(() => []),
    });
  };

  const handleDayChange = (e) => {
    const newDays = Number(e.target.value);
    const updated = Array(newDays)
      .fill(null)
      .map(() => []);
    setDetailsData({ ...detailsData, days: newDays, dayImages: updated });
  };

  const handleImageUpload = (dayIndex, files) => {
    const newImages = Array.from(files).map((file) => ({
      file,
      imageName: "",
      rating: 0,
      review: 0,
      details: "",
      preview: URL.createObjectURL(file),
    }));

    const updated = [...detailsData.dayImages];
    updated[dayIndex] = newImages;
    setDetailsData({ ...detailsData, dayImages: updated });
  };

  const updateImageField = (dayIndex, imgIndex, field, value) => {
    const updated = [...detailsData.dayImages];
    updated[dayIndex][imgIndex][field] = value;
    setDetailsData({ ...detailsData, dayImages: updated });
  };

  const handleSaveDetails = async () => {
    const formData = new FormData();
    if (detailsData.mainImage) formData.append("mainImage", detailsData.mainImage);
    formData.append("days", detailsData.days);

    detailsData.dayImages.forEach((dayImgs, dayIndex) => {
      dayImgs.forEach((img, imgIndex) => {
        formData.append(`details[${dayIndex}][${imgIndex}][imageName]`, img.imageName);
        formData.append(`details[${dayIndex}][${imgIndex}][rating]`, img.rating);
        formData.append(`details[${dayIndex}][${imgIndex}][review]`, img.review);
        formData.append(`details[${dayIndex}][${imgIndex}][details]`, img.details);
        formData.append(`details[${dayIndex}][${imgIndex}][file]`, img.file);
      });
    });

    try {
      const res = await fetch(`${apiURL}/packages/${editingId}/details`, {
        method: "POST",
        body: formData,
      });
      if (!res.ok) throw new Error("Failed to save details");
      alert("‚úÖ Details saved successfully!");
      setDetailsFormOpen(false);
      fetchPackages();
    } catch (err) {
      console.error(err);
      alert("Error saving details");
    }
  };

  const filteredPackages = packages.filter(
    (pkg) => pkg.type === activeTab
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="package-grid">
        {filteredPackages.length ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && <img src={pkg.image} alt={pkg.title} className="package-img" />}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <div className="card-actions">
                  <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>‚úè Edit</button>
                  <button className="remove-btn" onClick={() => handleRemovePackage(pkg._id)}>‚ùå Remove</button>
                  <button className="view-btn" onClick={() => handleViewDetails(pkg)}>üëÅ View</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available</p>
        )}
      </div>

      {/* üì¶ View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal">
            <h2>{viewPackage.title}</h2>
            <img src={viewPackage.image} alt={viewPackage.title} className="package-img" />
            <p><strong>Type:</strong> {viewPackage.type}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> ‚Çπ{viewPackage.price}</p>
            <p><strong>Rating:</strong> {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <div className="modal-actions">
              <button className="save-btn" onClick={() => openDetailsForm(viewPackage)}>üìù Details</button>
              <button className="cancel-btn" onClick={closeViewDetails}>‚ùå Close</button>
            </div>
          </div>
        </div>
      )}

      {/* üìù Details Form Modal */}
      {detailsFormOpen && (
        <div className="modal-overlay">
          <div className="modal large-modal">
            <h2>üìù Package Details</h2>
            <div className="details-form-scroll">
              <label className="input-label">Main Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setDetailsData({ ...detailsData, mainImage: e.target.files[0] })}
              />
              <label className="input-label">Total Days:</label>
              <input type="number" min="1" value={detailsData.days} onChange={handleDayChange} />

              {Array.from({ length: detailsData.days }).map((_, dayIndex) => (
                <div key={dayIndex} className="day-card">
                  <h4>üìÖ Day {dayIndex + 1}</h4>
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) => handleImageUpload(dayIndex, e.target.files)}
                  />

                  {detailsData.dayImages[dayIndex]?.map((img, imgIndex) => (
                    <div key={imgIndex} className="image-entry">
                      {img.preview && (
                        <img src={img.preview} alt="preview" className="preview-thumb" />
                      )}
                      <div className="image-entry-grid">
                        <input
                          type="text"
                          placeholder="Image Name"
                          value={img.imageName}
                          onChange={(e) => updateImageField(dayIndex, imgIndex, "imageName", e.target.value)}
                        />
                        <input
                          type="number"
                          placeholder="‚≠ê Rating"
                          min="0"
                          max="5"
                          step="0.1"
                          value={img.rating}
                          onChange={(e) => updateImageField(dayIndex, imgIndex, "rating", e.target.value)}
                        />
                        <input
                          type="number"
                          placeholder="üìù Review (number)"
                          value={img.review}
                          onChange={(e) => updateImageField(dayIndex, imgIndex, "review", e.target.value)}
                        />
                      </div>
                      <textarea
                        placeholder="Image Details..."
                        value={img.details}
                        onChange={(e) => updateImageField(dayIndex, imgIndex, "details", e.target.value)}
                      />
                    </div>
                  ))}
                </div>
              ))}
            </div>

            <div className="modal-actions sticky-actions">
              <button className="save-btn" onClick={handleSaveDetails}>üíæ Save Details</button>
              <button className="cancel-btn" onClick={() => setDetailsFormOpen(false)}>‚ùå Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
