import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]); 
  const [selectedSubcategory, setSelectedSubcategory] = useState(""); 
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false, // üÜï added field
  });

  /* Disable scroll when modal open */
  useEffect(() => {
    if (formOpen || viewPackage) document.body.style.overflow = "hidden";
    else document.body.style.overflow = "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage]);

  /* Fetch Data */
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
      setPackages([]);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
      setCategories([]);
    }
  };

  /* Add or Update Package */
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.price) {
      alert("Please fill Title and Price fields!");
      return;
    }

    if (newPackage.type === "Overseas" && !newPackage.country) {
      alert("Please enter Country for Overseas packages!");
      return;
    }

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        if (newPackage[key] !== undefined && newPackage[key] !== null) {
          formData.append(key, newPackage[key]);
        }
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  /* Delete Package */
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  /* Edit */
  const handleEditPackage = (pkg) => {
    setNewPackage({
      title: pkg.title,
      type: pkg.type,
      category: pkg.category?._id || "",
      price: pkg.price,
      rating: pkg.rating,
      review: pkg.review,
      country: pkg.country,
      showOnHome: pkg.showOnHome || false, // ‚úÖ keep this field when editing
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  /* View */
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  /* Reset */
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  /* Filter packages by active tab */
  const filteredPackages = packages.filter(
    (pkg) => pkg.type === activeTab
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                {pkg.type === "Overseas" && (
                  <p className="country-text">üåç {pkg.country}</p>
                )}
                <p>
                  üí∞{" "}
                  {pkg.price?.toString().startsWith("‚Çπ")
                    ? pkg.price
                    : `‚Çπ${pkg.price}`}
                </p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>
                  üè† Show on Home:{" "}
                  <strong style={{ color: pkg.showOnHome ? "green" : "red" }}>
                    {pkg.showOnHome ? "Yes" : "No"}
                  </strong>
                </p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    ‚úè Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    ‚ùå Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    üëÅ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />

              {/* Main Category */}
              <select
                value={newPackage.type}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    type: e.target.value,
                  })
                }
              >
                <option value=""></option>
                <option value="Domestic">Domestic</option>
                <option value="Overseas">Overseas</option>
              </select>

              {/* Dynamic Subcategory */}
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    category: e.target.value,
                  })
                }
              >
                <option value="">Select Subcategory</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.title}
                  </option>
                ))}
              </select>

              {newPackage.type === "Overseas" && (
                <input
                  type="text"
                  placeholder="Country (e.g. Japan)"
                  value={newPackage.country}
                  onChange={(e) =>
                    setNewPackage({ ...newPackage, country: e.target.value })
                  }
                />
              )}

              <input
                type="text"
                placeholder="Price (‚Çπ25,000 per person)"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    rating: parseFloat(e.target.value),
                  })
                }
              />
              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, review: e.target.value })
                }
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files[0])}
              />

              {/* üè† Show on Home */}
              <div className="show-home-toggle">
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    value="true"
                    checked={newPackage.showOnHome === true}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: true })
                    }
                  />{" "}
                  Show on Home
                </label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    value="false"
                    checked={newPackage.showOnHome === false}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: false })
                    }
                  />{" "}
                  Hide from Home
                </label>
              </div>
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* View Details Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{viewPackage.title}</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p>
              <strong>Main Category:</strong> {viewPackage.type}
            </p>
            <p>
              <strong>Subcategory:</strong> {viewPackage.category?.title}
            </p>
            {viewPackage.type === "Overseas" && (
              <p>
                <strong>Country:</strong> {viewPackage.country}
              </p>
            )}
            <p>
              <strong>Price:</strong> ‚Çπ{viewPackage.price}
            </p>
            <p>
              <strong>Rating:</strong> {viewPackage.rating}
            </p>
            <p>
              <strong>Review:</strong> {viewPackage.review}
            </p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "‚úÖ Yes" : "‚ùå No"}
            </p>
            <div className="modal-actions">
              <button onClick={closeViewDetails} className="cancel-btn">
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
