import React, { useState } from 'react';
import './Packages.css';

export default function Packages() {
  const [activeTab, setActiveTab] = useState('Domestic');
  const [packages, setPackages] = useState([
    { id: Date.now() + 1, name: 'Dubai Luxury Tour', price: '₹1,20,000', duration: '5N/6D', category: 'International' },
    { id: Date.now() + 2, name: 'Kerala Backwaters', price: '₹25,000', duration: '3N/4D', category: 'Domestic' },
    { id: Date.now() + 3, name: 'Goa Beach Trip', price: '₹15,000', duration: '2N/3D', category: 'Domestic' },
    { id: Date.now() + 4, name: 'Paris Romantic Tour', price: '₹2,50,000', duration: '6N/7D', category: 'International' },
  ]);

  const [newPackage, setNewPackage] = useState({
    name: '',
    price: '',
    duration: '',
    category: 'Domestic'
  });

  const filteredPackages = packages.filter(pkg => pkg.category === activeTab);

  const handleAddPackage = () => {
    if (!newPackage.name || !newPackage.price || !newPackage.duration) return;
    const id = Date.now(); // unique id
    setPackages([...packages, { ...newPackage, id }]);
    setNewPackage({ name: '', price: '', duration: '', category: 'Domestic' });
  };

  const handleRemovePackage = (id) => {
    // Optional: confirmation before removing
    if (window.confirm('Are you sure you want to remove this package?')) {
      setPackages(packages.filter(pkg => pkg.id !== id));
    }
  };

  return (
    <div className="packages-container">
      <h1>Travel Packages</h1>

      <div className="tabs">
        {['Domestic', 'International'].map(tab => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="package-list">
        {filteredPackages.map(pkg => (
          <div key={pkg.id} className="package-card">
            <h3>{pkg.name}</h3>
            <p><strong>Price:</strong> {pkg.price}</p>
            <p><strong>Duration:</strong> {pkg.duration}</p>
            <button
              className="remove-btn"
              onClick={() => handleRemovePackage(pkg.id)}
            >
              Remove
            </button>
          </div>
        ))}
      </div>

      <div className="add-package-form">
        <h2>Add New Package</h2>
        <div className="add-package-card">
          <input
            type="text"
            placeholder="Package Name"
            value={newPackage.name}
            onChange={e => setNewPackage({ ...newPackage, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Price"
            value={newPackage.price}
            onChange={e => setNewPackage({ ...newPackage, price: e.target.value })}
          />
          <input
            type="text"
            placeholder="Duration"
            value={newPackage.duration}
            onChange={e => setNewPackage({ ...newPackage, duration: e.target.value })}
          />
          <select
            value={newPackage.category}
            onChange={e => setNewPackage({ ...newPackage, category: e.target.value })}
          >
            <option value="Domestic">Domestic</option>
            <option value="International">International</option>
          </select>
          <button className="add-btn" onClick={handleAddPackage}>Add Package</button>
        </div>
      </div>
    </div>
  );
}
