import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  // üÜï Detail modal states
  const [detailFormOpen, setDetailFormOpen] = useState(false);
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: "",
    dayWiseImages: [],
    imageDetails: "",
    rating: "",
    review: "",
  });

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // üß© Disable scroll when modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailFormOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailFormOpen]);

  // üß© Fetch data
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // üß© Add/Update
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        if (newPackage[key] !== undefined && newPackage[key] !== null) {
          formData.append(key, newPackage[key]);
        }
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  // üß© Delete
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  // üß© Edit
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  // üß© View
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  // üß© Reset
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  // üß© Update days ‚Üí auto-generate day sections
  const handleDaysChange = (value) => {
    const numDays = parseInt(value);
    const dayWiseImages = Array.from({ length: numDays }, (_, i) => ({
      day: i + 1,
      images: [],
      imageNames: [""],
    }));
    setDetailData({ ...detailData, days: value, dayWiseImages });
  };

  // üß© Handle images per day
  const handleDayImageChange = (dayIndex, files) => {
    const newDays = [...detailData.dayWiseImages];
    newDays[dayIndex].images = Array.from(files);
    setDetailData({ ...detailData, dayWiseImages: newDays });
  };

  // üß© Handle image name change per day
  const handleImageNameChange = (dayIndex, imageIndex, value) => {
    const newDays = [...detailData.dayWiseImages];
    newDays[dayIndex].imageNames[imageIndex] = value;
    setDetailData({ ...detailData, dayWiseImages: newDays });
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>
                  üè† Show on Home: {pkg.showOnHome ? "‚úÖ Yes" : "‚ùå No"}
                </p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    ‚úè Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg._id)}
                  >
                    ‚ùå Remove
                  </button>
                  <button
                    className="view-btn"
                    onClick={() => handleViewDetails(pkg)}
                  >
                    üëÅ View
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available.</p>
        )}
      </div>

      {/* Add/Edit Modal */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              <div className="radio-group">
                <label>Show on Home:</label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === true}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: true })
                    }
                  />
                  Yes
                </label>
                <label>
                  <input
                    type="radio"
                    name="showOnHome"
                    checked={newPackage.showOnHome === false}
                    onChange={() =>
                      setNewPackage({ ...newPackage, showOnHome: false })
                    }
                  />
                  No
                </label>
              </div>

              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />

              <select
                value={newPackage.type}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, type: e.target.value })
                }
              >
                <option value="">Select Type</option>
                <option value="Domestic">Domestic</option>
                <option value="Overseas">Overseas</option>
              </select>

              <input
                type="text"
                placeholder="Country"
                value={newPackage.country}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, country: e.target.value })
                }
              />

              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                <option value="">Select Subcategory</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.title}
                  </option>
                ))}
              </select>

              <input
                type="text"
                placeholder="Price (‚Çπ)"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />

              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={newPackage.rating}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    rating: parseFloat(e.target.value),
                  })
                }
              />

              <input
                type="text"
                placeholder="Review"
                value={newPackage.review}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, review: e.target.value })
                }
              />

              <input
                type="file"
                accept="image/*"
                onChange={(e) => setImageFile(e.target.files[0])}
              />
            </div>

            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
              <button onClick={resetForm} className="cancel-btn">
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* üÜï Detail Form Modal */}
      {detailFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>üìù Add Package Details</h2>
            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              <label>Main Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  setDetailData({ ...detailData, mainImage: e.target.files[0] })
                }
              />

              <label>Days:</label>
              <input
                type="number"
                min="1"
                placeholder="Number of Days"
                value={detailData.days}
                onChange={(e) => handleDaysChange(e.target.value)}
              />

              {detailData.dayWiseImages.map((day, i) => (
                <div key={i} className="day-section">
                  <h4>üìÖ Day {day.day}</h4>
                  <label>Upload Images:</label>
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) => handleDayImageChange(i, e.target.files)}
                  />
                  <label>Image Names:</label>
                  {day.images.length > 0 &&
                    day.images.map((_, idx) => (
                      <input
                        key={idx}
                        type="text"
                        placeholder={`Name for image ${idx + 1}`}
                        value={day.imageNames[idx] || ""}
                        onChange={(e) =>
                          handleImageNameChange(i, idx, e.target.value)
                        }
                      />
                    ))}
                </div>
              ))}

              <label>Image Details:</label>
              <textarea
                placeholder="Describe images or highlights"
                value={detailData.imageDetails}
                onChange={(e) =>
                  setDetailData({ ...detailData, imageDetails: e.target.value })
                }
              ></textarea>

              <label>Rating:</label>
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                value={detailData.rating}
                onChange={(e) =>
                  setDetailData({ ...detailData, rating: e.target.value })
                }
              />

              <label>Review:</label>
              <textarea
                value={detailData.review}
                placeholder="Write review"
                onChange={(e) =>
                  setDetailData({ ...detailData, review: e.target.value })
                }
              ></textarea>
            </div>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  console.log("Saving Details:", detailData);
                  alert("‚úÖ Details saved locally (API connect next)");
                  setDetailFormOpen(false);
                }}
              >
                üíæ Save Details
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailFormOpen(false)}
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
