import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [existingDetails, setExistingDetails] = useState([]);

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
    days: "",
  });

  // üß© Detail structure per day
  const [dayWiseDetails, setDayWiseDetails] = useState({});

  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailForm ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailForm]);

  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // ‚ú® Add / Update Package
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");
    if (!newPackage.days) return alert("Please enter number of Days!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        formData.append(key, newPackage[key]);
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
      days: "",
    });
  };

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ DETAIL FORM ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const handleOpenDetailForm = (pkg) => {
    const days = parseInt(pkg.days || 0);
    if (!days || days < 1) return alert("Invalid number of days.");

    // üß© Build empty structure for each day
    const structure = {};
    for (let i = 1; i <= days; i++) {
      structure[`Day ${i}`] = [];
    }
    setDayWiseDetails(structure);
    setDetailForm(pkg);
  };

  const handleAddImageForDay = (day, files) => {
    const arr = Array.from(files).map((file) => ({
      image: file,
      imageName: file.name.replace(/\.[^/.]+$/, ""),
      rating: 4.5,
      review: "",
      imageDetail: "",
    }));

    setDayWiseDetails((prev) => ({
      ...prev,
      [day]: [...(prev[day] || []), ...arr],
    }));
  };

  const handleSavePackageDetail = async () => {
    if (!detailForm?._id) return alert("Package ID missing!");

    // Flatten all days into one payload
    const allImages = [];
    Object.entries(dayWiseDetails).forEach(([day, imgs]) => {
      imgs.forEach((img) => {
        allImages.push({
          ...img,
          day,
        });
      });
    });

    if (allImages.length === 0) {
      alert("Please upload at least one image across days.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("packageID", detailForm._id);
      formData.append(
        "imagesData",
        JSON.stringify(
          allImages.map((i) => ({
            imageName: i.imageName,
            rating: i.rating,
            review: i.review,
            imageDetail: i.imageDetail,
            day: i.day,
          }))
        )
      );

      allImages.forEach((img) => formData.append("images", img.image));

      const response = await fetch(`${apiURL}/package-details`, {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) {
        alert("‚ùå Failed to save details: " + (data.message || "Unknown error"));
        return;
      }

      alert("‚úÖ Package Details Saved Successfully!");
    } catch (error) {
      console.error(error);
      alert("Error saving package detail!");
    }
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && <img src={pkg.image} alt={pkg.title} className="package-img" />}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üåç {pkg.country}</p>
                <p>üïí {pkg.days} Days</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <div className="card-actions">
                  <button className="edit-btn" onClick={() => handleEditPackage(pkg)}>
                    ‚úè Edit
                  </button>
                  <button className="remove-btn" onClick={() => handleRemovePackage(pkg._id)}>
                    ‚ùå Remove
                  </button>
                  <button className="view-btn" onClick={() => handleOpenDetailForm(pkg)}>
                    üóì Add Details
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available.</p>
        )}
      </div>

      {/* Detail Form (Day-wise) */}
      {detailForm && (
        <div className="modal-overlay">
          <div className="modal" style={{ maxWidth: "900px", maxHeight: "85vh", overflowY: "auto" }}>
            <h2>üóì Add Details for {detailForm.days} Days</h2>

            {Object.keys(dayWiseDetails).map((day, idx) => (
              <div
                key={idx}
                style={{
                  border: "2px solid #ddd",
                  borderRadius: 10,
                  marginTop: 20,
                  padding: 15,
                }}
              >
                <h3>üìÖ {day}</h3>

                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => handleAddImageForDay(day, e.target.files)}
                />

                {(dayWiseDetails[day] || []).map((img, index) => (
                  <div
                    key={index}
                    style={{
                      border: "1px solid #ccc",
                      borderRadius: 10,
                      padding: 10,
                      marginTop: 10,
                    }}
                  >
                    <p style={{ fontWeight: "bold" }}>üñº {img.image.name}</p>

                    <input
                      type="text"
                      placeholder="Image Name"
                      value={img.imageName}
                      onChange={(e) => {
                        const updated = { ...dayWiseDetails };
                        updated[day][index].imageName = e.target.value;
                        setDayWiseDetails(updated);
                      }}
                      style={{ width: "100%", marginBottom: 8 }}
                    />

                    <input
                      type="number"
                      placeholder="Rating (1‚Äì5)"
                      value={img.rating}
                      min="1"
                      max="5"
                      step="0.1"
                      onChange={(e) => {
                        const updated = { ...dayWiseDetails };
                        updated[day][index].rating = e.target.value;
                        setDayWiseDetails(updated);
                      }}
                      style={{ width: "100%", marginBottom: 8 }}
                    />

                    <input
                      type="text"
                      placeholder="Review"
                      value={img.review}
                      onChange={(e) => {
                        const updated = { ...dayWiseDetails };
                        updated[day][index].review = e.target.value;
                        setDayWiseDetails(updated);
                      }}
                      style={{ width: "100%", marginBottom: 8 }}
                    />

                    <textarea
                      placeholder="Image Detail"
                      value={img.imageDetail}
                      onChange={(e) => {
                        const updated = { ...dayWiseDetails };
                        updated[day][index].imageDetail = e.target.value;
                        setDayWiseDetails(updated);
                      }}
                      style={{ width: "100%", minHeight: "60px" }}
                    ></textarea>
                  </div>
                ))}
              </div>
            ))}

            <div className="modal-actions" style={{ marginTop: 20 }}>
              <button className="save-btn" onClick={handleSavePackageDetail}>
                ‚úÖ Save All Details
              </button>
              <button className="cancel-btn" onClick={() => setDetailForm(null)}>
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
