import React, { useState } from 'react';
import './Packages.css';

export default function Packages() {
  const [packages, setPackages] = useState([
    { id: 1, name: 'Dubai Luxury Tour', price: '₹1,20,000', duration: '5N/6D', category: 'International' },
    { id: 2, name: 'Kerala Backwaters', price: '₹25,000', duration: '3N/4D', category: 'Domestic' },
    { id: 3, name: 'Japan Adventure', price: '₹2,50,000', duration: '5N/6D', category: 'International' },
  ]);

  const [form, setForm] = useState({
    name: '',
    price: '',
    duration: '',
    category: 'Domestic'
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.name || !form.price || !form.duration) return;

    const newPackage = {
      id: packages.length + 1,
      ...form
    };

    setPackages([...packages, newPackage]);
    setForm({ name: '', price: '', duration: '', category: 'Domestic' });
  };

  return (
    <div className="page packages-page">
      <h2>Packages</h2>

      {/* Add Package Form */}
      <form className="package-form" onSubmit={handleSubmit}>
        <input 
          type="text" 
          name="name" 
          placeholder="Package Name" 
          value={form.name} 
          onChange={handleChange} 
        />
        <input 
          type="text" 
          name="price" 
          placeholder="Price (₹)" 
          value={form.price} 
          onChange={handleChange} 
        />
        <input 
          type="text" 
          name="duration" 
          placeholder="Duration (e.g. 5N/6D)" 
          value={form.duration} 
          onChange={handleChange} 
        />
        <select name="category" value={form.category} onChange={handleChange}>
          <option value="Domestic">Domestic</option>
          <option value="International">International</option>
        </select>
        <button type="submit">Add Package</button>
      </form>

      {/* Packages Grid */}
      <div className="package-grid">
        {packages.map(p => (
          <div key={p.id} className="package-card">
            <div className="package-header">
              <h3>{p.name}</h3>
              <span className={`category ${p.category.toLowerCase()}`}>{p.category}</span>
            </div>
            <div className="package-body">
              <p><strong>Duration:</strong> {p.duration}</p>
              <p><strong>Price:</strong> {p.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
