import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Packages.css";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [newPackage, setNewPackage] = useState({
    title: "",
    description: "",
    destination: "",   
    category: "domestic", 
    duration: "",
    price: "",
    inclusions: "",
    exclusions: "",
    availability: "",
    rating: "",
    status: "active", 
    state: "",
    touristPlaces: "", 
    images: []         
  });

  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/packages");
      setPackages(res.data.data || []);
    } catch (err) {
      console.error("Error fetching packages", err);
    }
  };

  const handleAddPackage = async () => {
    try {
      const payload = {
        ...newPackage,
        price: Number(newPackage.price),
        rating: Number(newPackage.rating),
        inclusions: newPackage.inclusions.split(",").map((s) => s.trim()),
        exclusions: newPackage.exclusions.split(",").map((s) => s.trim()),
        touristPlaces: newPackage.touristPlaces.split(",").map((s) => s.trim()),
        images: newPackage.images
      };

      const res = await axios.post("http://localhost:5000/api/packages", payload);
      if (res.data.result === "success") {
        setPackages([...packages, res.data.data]);
        setNewPackage({
          title: "",
          description: "",
          destination: "",
          category: "domestic",
          duration: "",
          price: "",
          inclusions: "",
          exclusions: "",
          availability: "",
          rating: "",
          status: "active",
          state: "",
          touristPlaces: "",
          images: []
        });
      }
    } catch (err) {
      console.error("Error adding package", err);
    }
  };

  return (
    <div className="packages-container">
      <h2>Packages</h2>

      {/* Tabs */}
      <div className="tabs">
        <button onClick={() => setActiveTab("Domestic")}>Domestic</button>
        <button onClick={() => setActiveTab("International")}>International</button>
      </div>

      {/* Add Package Form */}
      <div className="add-package-form">
        <input type="text" placeholder="Title" value={newPackage.title}
          onChange={(e) => setNewPackage({ ...newPackage, title: e.target.value })} />
        <input type="text" placeholder="Description" value={newPackage.description}
          onChange={(e) => setNewPackage({ ...newPackage, description: e.target.value })} />
        <input type="text" placeholder="Destination" value={newPackage.destination}
          onChange={(e) => setNewPackage({ ...newPackage, destination: e.target.value })} />
        <input type="text" placeholder="Duration" value={newPackage.duration}
          onChange={(e) => setNewPackage({ ...newPackage, duration: e.target.value })} />
        <input type="number" placeholder="Price" value={newPackage.price}
          onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })} />
        <input type="text" placeholder="Inclusions (comma separated)" value={newPackage.inclusions}
          onChange={(e) => setNewPackage({ ...newPackage, inclusions: e.target.value })} />
        <input type="text" placeholder="Exclusions (comma separated)" value={newPackage.exclusions}
          onChange={(e) => setNewPackage({ ...newPackage, exclusions: e.target.value })} />
        <input type="text" placeholder="Availability" value={newPackage.availability}
          onChange={(e) => setNewPackage({ ...newPackage, availability: e.target.value })} />
        <input type="number" placeholder="Rating" value={newPackage.rating}
          onChange={(e) => setNewPackage({ ...newPackage, rating: e.target.value })} />
        <input type="text" placeholder="State ID" value={newPackage.state}
          onChange={(e) => setNewPackage({ ...newPackage, state: e.target.value })} />
        <input type="text" placeholder="Tourist Places (comma separated)" value={newPackage.touristPlaces}
          onChange={(e) => setNewPackage({ ...newPackage, touristPlaces: e.target.value })} />

        <input type="file" accept="image/*" multiple
          onChange={(e) => {
            const files = Array.from(e.target.files);
            const urls = files.map((file) => URL.createObjectURL(file));
            setNewPackage({ ...newPackage, images: urls });
          }} />

        <button onClick={handleAddPackage}>Add Package</button>
      </div>

      {/* Packages List */}
      <div className="packages-list">
        {packages
          .filter((pkg) => pkg.category.toLowerCase() === activeTab.toLowerCase())
          .map((pkg) => (
            <div key={pkg._id} className="package-card">
              <h3>{pkg.title}</h3>
              <p>{pkg.description}</p>
              <p><strong>Destination:</strong> {pkg.destination}</p>
              <p><strong>Price:</strong> â‚¹{pkg.price}</p>
              <p><strong>State:</strong> {pkg.state?.name}</p>
              <p><strong>Tourist Places:</strong> {pkg.touristPlaces?.join(", ")}</p>
              {pkg.images?.length > 0 && (
                <img src={pkg.images[0]} alt={pkg.title} width="150" />
              )}
            </div>
          ))}
      </div>
    </div>
  );
}
