import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [viewPackage, setViewPackage] = useState(null);
  const [detailForm, setDetailForm] = useState(null);
  const [detailData, setDetailData] = useState({
    mainImage: null,
    days: 1,
    dayDetails: [],
  });

  // üß≠ Fetch packages and categories
  useEffect(() => {
    fetch(`${apiURL}/package/type/${activeTab}`)
      .then((res) => res.json())
      .then((data) => setPackages(data))
      .catch((err) => console.error(err));

    fetch(`${apiURL}/category`)
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch((err) => console.error(err));
  }, [activeTab]);

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to delete this package?")) return;
    try {
      await fetch(`${apiURL}/package/${id}`, { method: "DELETE" });
      setPackages((prev) => prev.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error(error);
    }
  };

  const handleViewDetails = (pkg) => {
    setViewPackage(pkg);
  };

  const closeViewDetails = () => setViewPackage(null);

  const handleDaysChange = (e) => {
    const days = Number(e.target.value);
    setDetailData((prev) => ({
      ...prev,
      days,
      dayDetails: Array.from({ length: days }, (_, i) => prev.dayDetails[i] || {}),
    }));
  };

  const handleImageChange = (index, files) => {
    const updatedDetails = [...detailData.dayDetails];
    updatedDetails[index].images = Array.from(files);
    setDetailData({ ...detailData, dayDetails: updatedDetails });
  };

  const handleDetailChange = (index, field, value) => {
    const updated = [...detailData.dayDetails];
    updated[index] = { ...updated[index], [field]: value };
    setDetailData({ ...detailData, dayDetails: updated });
  };

  return (
    <div className="packages-page">
      <h1>üì¶ Packages</h1>

      {/* Tabs */}
      <div className="tab-buttons">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? "active" : ""}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Cards */}
      <div className="package-grid">
        {packages.map((pkg) => (
          <div className="package-card" key={pkg._id}>
            <h3>{pkg.title}</h3>
            <p>üìç {pkg.category?.title}</p>
            <p>üåé {pkg.country}</p>
            <p>üí∞ ‚Çπ{pkg.price}</p>
            <p>‚≠ê {pkg.rating}</p>
            <p>Review: {pkg.review}</p>
            <p>
              üè° Show on Home: {pkg.showOnHome ? "‚úÖ" : "‚ùå"}
            </p>

            <div className="card-actions">
              <button className="edit-btn" onClick={() => alert("Edit feature soon!")}>
                ‚úè Edit
              </button>
              <button
                className="remove-btn"
                onClick={() => handleRemovePackage(pkg._id)}
              >
                ‚ùå Remove
              </button>
              <button className="view-btn" onClick={() => handleViewDetails(pkg)}>
                üëÅ View
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* üëÅ View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal view-modal" style={{ maxWidth: "600px" }}>
            <h2>üëÅ View Package</h2>
            <p><strong>Title:</strong> {viewPackage.title}</p>
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> ‚Çπ{viewPackage.price}</p>
            <p><strong>Rating:</strong> ‚≠ê {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <p>
              <strong>Show on Home:</strong>{" "}
              {viewPackage.showOnHome ? "‚úÖ Yes" : "‚ùå No"}
            </p>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setViewPackage(null);
                  setDetailForm(viewPackage);
                }}
              >
                üìã Detail
              </button>
              <button className="cancel-btn" onClick={closeViewDetails}>
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* üìã Detail Form */}
      {detailForm && (
        <div className="modal-overlay">
          <div
            className="modal"
            style={{
              maxWidth: "700px",
              maxHeight: "80vh",
              overflowY: "auto",
              paddingBottom: "20px",
            }}
          >
            <h2 style={{ textAlign: "center", marginBottom: "15px" }}>
              üìã Package Detail Form
            </h2>

            <div
              className="form-grid"
              style={{
                gridTemplateColumns: "1fr 1fr",
                gap: "15px",
              }}
            >
              {/* üåÑ Main Image */}
              <div style={{ gridColumn: "span 2" }}>
                <label style={{ fontWeight: "600" }}>Main Image:</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) =>
                    setDetailData({ ...detailData, mainImage: e.target.files[0] })
                  }
                />
              </div>

              {/* üìÖ Number of Days */}
              <div style={{ gridColumn: "span 2" }}>
                <label style={{ fontWeight: "600" }}>Number of Days:</label>
                <input
                  type="number"
                  min="1"
                  placeholder="Number of Days"
                  value={detailData.days}
                  onChange={handleDaysChange}
                />
              </div>

              {/* üß© Dynamic Days */}
              {detailData.dayDetails.map((day, index) => (
                <div
                  key={index}
                  style={{
                    gridColumn: "span 2",
                    border: "2px solid #0072ff",
                    borderRadius: "12px",
                    padding: "15px",
                    marginBottom: "15px",
                    background: "#f7faff",
                  }}
                >
                  <h3
                    style={{
                      color: "#0072ff",
                      fontWeight: "700",
                      marginBottom: "10px",
                    }}
                  >
                    üóìÔ∏è Day {index + 1}
                  </h3>

                  <label style={{ fontWeight: "600" }}>Upload Images:</label>
                  <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={(e) => handleImageChange(index, e.target.files)}
                  />

                  <label style={{ fontWeight: "600" }}>Images Name:</label>
                  <input
                    type="text"
                    placeholder="Enter image names (comma separated)"
                    value={day.imagesName || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "imagesName", e.target.value)
                    }
                  />

                  <label style={{ fontWeight: "600" }}>Rating:</label>
                  <input
                    type="number"
                    placeholder="Rating (0-5)"
                    value={day.rating || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "rating", e.target.value)
                    }
                  />

                  <label style={{ fontWeight: "600" }}>Review:</label>
                  <input
                    type="number"
                    placeholder="Review count"
                    value={day.review || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "review", e.target.value)
                    }
                  />

                  <label style={{ fontWeight: "600" }}>Image Details:</label>
                  <textarea
                    placeholder="Describe the images for this day..."
                    value={day.imageDetails || ""}
                    onChange={(e) =>
                      handleDetailChange(index, "imageDetails", e.target.value)
                    }
                    style={{ resize: "none", minHeight: "80px" }}
                  />
                </div>
              ))}
            </div>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  alert("Detail saved successfully! üéâ");
                  setDetailForm(null);
                }}
              >
                ‚úÖ Save
              </button>
              <button className="cancel-btn" onClick={() => setDetailForm(null)}>
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
