  import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  // Detail modal
  const [detailFormOpen, setDetailFormOpen] = useState(false);
  const [detailData, setDetailData] = useState({
    days: "",
    itinerary: [],
    imageDetails: "",
    rating: "",
    review: "",
  });

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // Disable scroll when modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailFormOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailFormOpen]);

  // Fetch data
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // Add / update package
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        if (newPackage[key] !== undefined && newPackage[key] !== null) {
          formData.append(key, newPackage[key]);
        }
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  // Remove package
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  // Edit
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  // View
  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  // Reset form
  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  // Filter packages
  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  // üÜï Handle Days input change and auto-generate itinerary blocks
  const handleDaysChange = (daysValue) => {
    const numberOfDays = parseInt(daysValue) || 0;
    const newItinerary = Array.from({ length: numberOfDays }, (_, i) => ({
      dayNumber: i + 1,
      images: [],
      imageNames: [],
    }));
    setDetailData({
      ...detailData,
      days: numberOfDays,
      itinerary: newItinerary,
    });
  };

  // üß© Handle file upload for specific day
  const handleDayImageUpload = (index, files) => {
    const updated = [...detailData.itinerary];
    updated[index].images = Array.from(files);
    setDetailData({ ...detailData, itinerary: updated });
  };

  // üß© Handle image name input for specific day
  const handleImageNameChange = (index, value) => {
    const updated = [...detailData.itinerary];
    updated[index].imageNames = value.split(",").map((v) => v.trim());
    setDetailData({ ...detailData, itinerary: updated });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>üè† Show on Home: {pkg.showOnHome ? "‚úÖ Yes" : "‚ùå No"}</p>
                <div className="card-actions">
                  <button onClick={() => handleEditPackage(pkg)}>‚úè Edit</button>
                  <button onClick={() => handleRemovePackage(pkg._id)}>
                    ‚ùå Remove
                  </button>
                  <button onClick={() => handleViewDetails(pkg)}>üëÅ View</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available.</p>
        )}
      </div>

      {/* üÜï View Modal */}
      {viewPackage && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{viewPackage.title}</h2>
            {viewPackage.image && (
              <img
                src={viewPackage.image}
                alt={viewPackage.title}
                className="package-img"
              />
            )}
            <p><strong>Category:</strong> {viewPackage.category?.title}</p>
            <p><strong>Country:</strong> {viewPackage.country}</p>
            <p><strong>Price:</strong> ‚Çπ{viewPackage.price}</p>
            <p><strong>Rating:</strong> {viewPackage.rating}</p>
            <p><strong>Review:</strong> {viewPackage.review}</p>
            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  setDetailData({
                    days: "",
                    itinerary: [],
                    imageDetails: "",
                    rating: "",
                    review: "",
                  });
                  setDetailFormOpen(true);
                }}
              >
                üìã Add Details
              </button>
              <button onClick={closeViewDetails} className="cancel-btn">
                ‚ùå Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* üß© Detail Form Modal */}
      {detailFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>üìù Add Day-wise Details</h2>

            <div
              className="form-grid"
              style={{ maxHeight: "70vh", overflowY: "auto" }}
            >
              <label>Total Days:</label>
              <input
                type="number"
                min="1"
                placeholder="Enter number of days"
                value={detailData.days}
                onChange={(e) => handleDaysChange(e.target.value)}
              />

              {/* Dynamically Render Day Sections */}
              {detailData.itinerary.map((day, index) => (
                <div key={index} className="day-section">
                  <h4>üìÖ Day {day.dayNumber}</h4>
                  <label>Upload Images:</label>
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) =>
                      handleDayImageUpload(index, e.target.files)
                    }
                  />
                  <label>Image Names (comma separated):</label>
                  <input
                    type="text"
                    placeholder="Beach, Hotel, Sunset..."
                    onChange={(e) =>
                      handleImageNameChange(index, e.target.value)
                    }
                  />
                </div>
              ))}

              <label>Trip Description:</label>
              <textarea
                placeholder="Describe trip highlights..."
                value={detailData.imageDetails}
                onChange={(e) =>
                  setDetailData({ ...detailData, imageDetails: e.target.value })
                }
              ></textarea>

              <label>Rating:</label>
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={detailData.rating}
                onChange={(e) =>
                  setDetailData({ ...detailData, rating: e.target.value })
                }
              />

              <label>Review:</label>
              <textarea
                placeholder="Write a short review"
                value={detailData.review}
                onChange={(e) =>
                  setDetailData({ ...detailData, review: e.target.value })
                }
              ></textarea>
            </div>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  console.log("üßæ Saved Details:", detailData);
                  alert("‚úÖ Day-wise details saved (connect API next)");
                  setDetailFormOpen(false);
                }}
              >
                üíæ Save
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailFormOpen(false)}
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
