import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [formValues, setFormValues] = useState({
    state: "",
    touristPlace: "",
    description: "",
    duration: "",
    price: "",
    hotelName: "",
    transportType: "",
    foodIncluded: false,
    sightseeing: false,
    inclusions: [],
    exclusions: [],
    imageFile: null,
  });

  // üßæ Fetch all packages
  const fetchPackages = async () => {
    try {
      const res = await fetch(`${apiURL}/api/packages`);
      const data = await res.json();
      if (data.success) setPackages(data.data);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  useEffect(() => {
    fetchPackages();
  }, [activeTab]);

  // üü¢ Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormValues((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  // üñºÔ∏è Handle image upload
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) setFormValues((prev) => ({ ...prev, imageFile: file }));
  };

  // üü¢ Handle inclusions/exclusions add/remove
  const handleArrayChange = (field, index, value) => {
    const arr = [...formValues[field]];
    arr[index] = value;
    setFormValues((prev) => ({ ...prev, [field]: arr }));
  };

  const addArrayItem = (field) => {
    setFormValues((prev) => ({
      ...prev,
      [field]: [...prev[field], ""],
    }));
  };

  const removeArrayItem = (field, index) => {
    const arr = [...formValues[field]];
    arr.splice(index, 1);
    setFormValues((prev) => ({ ...prev, [field]: arr }));
  };

  // üß© Edit existing package
  const handleEdit = (pkg) => {
    setEditingId(pkg._id);
    setFormValues({
      state: pkg.state || "",
      touristPlace: pkg.touristPlace || "",
      description: pkg.description || "",
      duration: pkg.duration || "",
      price: pkg.price || "",
      hotelName: pkg.hotelName || "",
      transportType: pkg.transportType || "",
      foodIncluded: pkg.foodIncluded || false,
      sightseeing: pkg.sightseeing || false,
      inclusions: pkg.inclusions || [],
      exclusions: pkg.exclusions || [],
      imageFile: null,
    });
    setFormOpen(true);
  };

  // üü¢ Delete package
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this package?")) return;
    try {
      const res = await fetch(`${apiURL}/api/packages/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      if (data.success) fetchPackages();
    } catch (error) {
      console.error("Delete error:", error);
    }
  };

  // üíæ Save or Update package
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    if (editingId) formData.append("id", editingId);
    formData.append("category", activeTab);
    formData.append("state", formValues.state);
    formData.append("touristPlace", formValues.touristPlace);
    formData.append("description", formValues.description);
    formData.append("duration", formValues.duration);
    formData.append("price", formValues.price);
    formData.append("hotelName", formValues.hotelName);
    formData.append("transportType", formValues.transportType);
    formData.append("foodIncluded", formValues.foodIncluded);
    formData.append("sightseeing", formValues.sightseeing);
    formData.append("inclusions", JSON.stringify(formValues.inclusions));
    formData.append("exclusions", JSON.stringify(formValues.exclusions));

    if (formValues.imageFile) {
      formData.append("image", formValues.imageFile);
    }

    try {
      const res = await fetch(`${apiURL}/api/packages`, {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      if (data.success) {
        alert("‚úÖ Package saved successfully!");
        setFormOpen(false);
        setEditingId(null);
        fetchPackages();
      } else {
        alert("‚ö†Ô∏è " + data.message);
      }
    } catch (error) {
      console.error("Save error:", error);
    }
  };

  // üß± Render
  return (
    <div className="packages-container">
      <h1>üì¶ Packages</h1>

      {/* Tabs */}
      <div className="tabs">
        <button
          className={activeTab === "Domestic" ? "active" : ""}
          onClick={() => setActiveTab("Domestic")}
        >
          Domestic
        </button>
        <button
          className={activeTab === "International" ? "active" : ""}
          onClick={() => setActiveTab("International")}
        >
          International
        </button>
      </div>

      <button className="add-btn" onClick={() => setFormOpen(true)}>
        ‚ûï Add Package
      </button>

      {/* Package List */}
      <div className="package-list">
        {packages
          .filter((p) => p.category === activeTab)
          .map((pkg) => (
            <div key={pkg._id} className="package-card">
              <img
                src={pkg.imageUrl ? `${apiURL}${pkg.imageUrl}` : "/noimage.png"}
                alt={pkg.touristPlace}
              />
              <div>
                <h3>{pkg.touristPlace}</h3>
                <p>{pkg.state}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <button onClick={() => handleEdit(pkg)}>‚úèÔ∏è Edit</button>
                <button onClick={() => handleDelete(pkg._id)}>üóëÔ∏è Delete</button>
              </div>
            </div>
          ))}
      </div>

      {/* Modal Form */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "‚úèÔ∏è Edit Package" : "‚ûï Add Package"}</h2>

            <form onSubmit={handleSubmit}>
              <label>State:</label>
              <input
                name="state"
                value={formValues.state}
