import React, { useState } from "react";
import "./Packages.css";

export default function Packages() {
  // Define all categories here
  const categories = [
    "Domestic",
    "International",
    "Honeymoon",
    "Adventure",
    "Family",
    "Luxury",
    "Road Trip",
    "Cultural",
  ];

  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([
    {
      id: Date.now() + 1,
      title: "Dubai Luxury Tour",
      description:
        "Experience luxury in Dubai with Burj Khalifa, Desert Safari, and shopping.",
      category: "International",
      duration: "5N/6D",
      price: "‚Çπ1,20,000",
      inclusions: "Flights, Hotels, Transfers, Sightseeing",
      exclusions: "Personal expenses, Visa fees",
      availability: "Available",
      rating: 4.8,
      status: "Active",
      state: "",
      touristPlace: "Dubai, Abu Dhabi",
      image: "https://example.com/dubai.jpg",
    },
    {
      id: Date.now() + 2,
      title: "Kerala Backwaters",
      description:
        "Enjoy a peaceful houseboat ride and Ayurvedic therapies in Kerala.",
      category: "Domestic",
      duration: "3N/4D",
      price: "‚Çπ25,000",
      inclusions: "Hotels, Breakfast, Transfers",
      exclusions: "Flights, Personal expenses",
      availability: "Available",
      rating: 4.5,
      status: "Active",
      state: "Kerala",
      touristPlace: "Alleppey, Kumarakom",
      image: "https://example.com/kerala.jpg",
    },
  ]);

  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [newPackage, setNewPackage] = useState({
    title: "",
    description: "",
    category: "Domestic",
    duration: "",
    price: "",
    inclusions: "",
    exclusions: "",
    availability: "",
    rating: "",
    status: "Active",
    state: "",
    touristPlace: "",
    image: "",
  });

  const filteredPackages = packages.filter(
    (pkg) => pkg.category === activeTab
  );

  const handleAddOrUpdatePackage = () => {
    if (!newPackage.title || !newPackage.price || !newPackage.duration)
      return;

    if (editingId) {
      setPackages(
        packages.map((pkg) =>
          pkg.id === editingId ? { ...newPackage, id: editingId } : pkg
        )
      );
      setEditingId(null);
    } else {
      const id = Date.now();
      setPackages([...packages, { ...newPackage, id }]);
    }

    resetForm();
  };

  const handleRemovePackage = (id) => {
    if (window.confirm("Are you sure you want to remove this package?")) {
      setPackages(packages.filter((pkg) => pkg.id !== id));
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage(pkg);
    setEditingId(pkg.id);
    setFormOpen(true);
  };

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setNewPackage({
      title: "",
      description: "",
      category: "Domestic",
      duration: "",
      price: "",
      inclusions: "",
      exclusions: "",
      availability: "",
      rating: "",
      status: "Active",
      state: "",
      touristPlace: "",
      image: "",
    });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {categories.map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Packages */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg.id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p className="desc">{pkg.description}</p>
                <p>
                  <strong>üìç {pkg.touristPlace || pkg.state}</strong>
                </p>
                <p>
                  <strong>üí∞ {pkg.price}</strong> ‚Ä¢ {pkg.duration}
                </p>
                <p>
                  <strong>‚≠ê</strong> {pkg.rating}
                </p>
                <div className="card-actions">
                  <button
                    className="edit-btn"
                    onClick={() => handleEditPackage(pkg)}
                  >
                    ‚úèÔ∏è Edit
                  </button>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemovePackage(pkg.id)}
                  >
                    ‚ùå Remove
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available in this category.</p>
        )}
      </div>

      {/* Modal Form */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid">
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, title: e.target.value })
                }
              />
              <textarea
                placeholder="Description"
                value={newPackage.description}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, description: e.target.value })
                }
              />
              <select
                value={newPackage.category}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, category: e.target.value })
                }
              >
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Duration"
                value={newPackage.duration}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, duration: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, price: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Inclusions"
                value={newPackage.inclusions}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, inclusions: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Exclusions"
                value={newPackage.exclusions}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, exclusions: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Availability"
                value={newPackage.availability}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    availability: e.target.value,
                  })
                }
              />
              <input
                type="number"
                placeholder="Rating"
                min="0"
                max="5"
                step="0.1"
                value={newPackage.rating}
                onChange={(e) => {
                  let value = parseFloat(e.target.value);
                  if (value > 5) value = 5;
                  if (value < 0) value = 0;
                  setNewPackage({ ...newPackage, rating: value });
                }}
              />
              <select
                value={newPackage.status}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, status: e.target.value })
                }
              >
                <option value="Active">Active</option>
                <option value="Inactive">Inactive</option>
              </select>
              <input
                type="text"
                placeholder="State"
                value={newPackage.state}
                onChange={(e) =>
                  setNewPackage({ ...newPackage, state: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="Tourist Place"
                value={newPackage.touristPlace}
                onChange={(e) =>
                  setNewPackage({
                    ...newPackage,
                    touristPlace: e.target.value,
                  })
                }
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    const imageUrl = URL.createObjectURL(file);
                    setNewPackage({ ...newPackage, image: imageUrl });
                  }
                }}
              />
            </div>
            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage} className="save-btn">
                {editingId ? "‚úÖ Update" : "‚ûï Add"}
              </button>
