// middleware/AdminRoute.js
import React from "react";
import { Navigate } from "react-router-dom";

// Simulated auth check function
// Replace with your real auth logic (e.g., from context or Redux)
const isAdmin = () => {
  // Example: check localStorage or token
  const user = JSON.parse(localStorage.getItem("user"));
  return user?.role === "admin"; // Make sure your user object has a 'role' field
};

const AdminRoute = ({ children }) => {
  if (!isAdmin()) {
    // If not admin, redirect to Not Authorized page
    return <Navigate to="/admin/not-authorized" replace />;
  }

  // If admin, render the children (AdminLayout with <Outlet />)
  return children;
};

export default AdminRoute;
