import React, { useState, useEffect } from "react";
import "./Packages.css";
import { apiURL } from "../services/variables.js";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("Domestic");
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [viewPackage, setViewPackage] = useState(null);

  // Detail Modal States
  const [detailFormOpen, setDetailFormOpen] = useState(false);
  const [detailData, setDetailData] = useState({
    days: 0,
    itinerary: [],
    imageDetails: "",
    rating: "",
    review: "",
  });

  const [newPackage, setNewPackage] = useState({
    title: "",
    type: "Domestic",
    category: "",
    price: "",
    rating: "",
    review: "",
    image: "",
    country: "",
    showOnHome: false,
  });

  // Disable scroll when modal open
  useEffect(() => {
    document.body.style.overflow =
      formOpen || viewPackage || detailFormOpen ? "hidden" : "auto";
    return () => (document.body.style.overflow = "auto");
  }, [formOpen, viewPackage, detailFormOpen]);

  // Fetch data
  useEffect(() => {
    fetchPackages();
    fetchCategories();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch(`${apiURL}/packages`);
      const data = await response.json();
      setPackages(data.data || []);
    } catch (error) {
      console.error("Error fetching packages:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch(`${apiURL}/categories`);
      const data = await response.json();
      if (data.result === "success") setCategories(data.data || []);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title) return alert("Please fill Title!");
    if (!newPackage.country) return alert("Please enter Country!");

    try {
      const method = editingId ? "PUT" : "POST";
      const url = editingId
        ? `${apiURL}/packages/${editingId}`
        : `${apiURL}/packages`;

      const formData = new FormData();
      for (const key in newPackage) {
        if (newPackage[key] !== undefined && newPackage[key] !== null) {
          formData.append(key, newPackage[key]);
        }
      }
      if (imageFile) formData.append("image", imageFile);

      const response = await fetch(url, { method, body: formData });
      const data = await response.json();

      if (!response.ok) {
        alert("‚ùå Error: " + (data.message || "Failed to save package"));
        return;
      }

      alert("‚úÖ Package saved successfully!");
      fetchPackages();
      resetForm();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Something went wrong while saving the package.");
    }
  };

  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to remove this package?")) return;
    try {
      await fetch(`${apiURL}/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (error) {
      console.error("Error deleting package:", error);
    }
  };

  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      category: pkg.category?._id || pkg.category,
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const handleViewDetails = (pkg) => setViewPackage(pkg);
  const closeViewDetails = () => setViewPackage(null);

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setImageFile(null);
    setNewPackage({
      title: "",
      type: "Domestic",
      category: "",
      price: "",
      rating: "",
      review: "",
      image: "",
      country: "",
      showOnHome: false,
    });
  };

  const filteredPackages = packages.filter(
    (pkg) => !selectedSubcategory || pkg.type === selectedSubcategory
  );

  // üß© Handle days change (dynamic day blocks)
  const handleDaysChange = (value) => {
    const daysCount = parseInt(value) || 0;
    const newItinerary = Array.from({ length: daysCount }, (_, i) => ({
      dayNumber: i + 1,
      images: [],
      imageNames: [],
    }));
    setDetailData({ ...detailData, days: daysCount, itinerary: newItinerary });
  };

  // üß© Handle file upload per day
  const handleDayImageUpload = (dayIndex, files) => {
    const updatedItinerary = [...detailData.itinerary];
    updatedItinerary[dayIndex].images = Array.from(files);
    setDetailData({ ...detailData, itinerary: updatedItinerary });
  };

  // üß© Handle image names per day
  const handleImageNameChange = (dayIndex, value) => {
    const updatedItinerary = [...detailData.itinerary];
    updatedItinerary[dayIndex].imageNames = value.split(",").map((v) => v.trim());
    setDetailData({ ...detailData, itinerary: updatedItinerary });
  };

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>
          ‚ûï Add Package
        </button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["Domestic", "Overseas"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => {
              setActiveTab(tab);
              setSelectedSubcategory("");
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Package Grid */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.image && (
                <img src={pkg.image} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>üè∑Ô∏è {pkg.category?.title}</p>
                <p>üåç {pkg.country}</p>
                <p>üí∞ ‚Çπ{pkg.price}</p>
                <p>‚≠ê {pkg.rating}</p>
                <p>Review: {pkg.review}</p>
                <p>üè† Show on Home: {pkg.showOnHome ? "‚úÖ Yes" : "‚ùå No"}</p>
                <div className="card-actions">
                  <button onClick={() => handleEditPackage(pkg)}>‚úè Edit</button>
                  <button onClick={() => handleRemovePackage(pkg._id)}>
                    ‚ùå Remove
                  </button>
                  <button onClick={() => handleViewDetails(pkg)}>üëÅ View</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-text">No packages available.</p>
        )}
      </div>

      {/* üÜï Detail Form Modal */}
      {detailFormOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>üìù Add Day-wise Package Details</h2>
            <div className="form-grid" style={{ maxHeight: "70vh", overflowY: "auto" }}>
              <label>Total Days:</label>
              <input
                type="number"
                min="1"
                placeholder="Number of Days"
                value={detailData.days}
                onChange={(e) => handleDaysChange(e.target.value)}
              />

              {detailData.itinerary.map((day, index) => (
                <div key={index} className="day-section">
                  <h4>üìÖ Day {day.dayNumber}</h4>
                  <label>Upload Images:</label>
                  <input
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={(e) =>
                      handleDayImageUpload(index, e.target.files)
                    }
                  />
                  <label>Image Names (comma separated):</label>
                  <input
                    type="text"
                    placeholder="e.g. Beach, Hotel, Sunset"
                    onChange={(e) =>
                      handleImageNameChange(index, e.target.value)
                    }
                  />
                </div>
              ))}

              <label>Overall Trip Description:</label>
              <textarea
                placeholder="Describe the trip highlights..."
                value={detailData.imageDetails}
                onChange={(e) =>
                  setDetailData({ ...detailData, imageDetails: e.target.value })
                }
              ></textarea>

              <label>Rating:</label>
              <input
                type="number"
                min="0"
                max="5"
                step="0.1"
                placeholder="Rating"
                value={detailData.rating}
                onChange={(e) =>
                  setDetailData({ ...detailData, rating: e.target.value })
                }
              />

              <label>Review:</label>
              <textarea
                placeholder="Write a review or summary"
                value={detailData.review}
                onChange={(e) =>
                  setDetailData({ ...detailData, review: e.target.value })
                }
              ></textarea>
            </div>

            <div className="modal-actions">
              <button
                className="save-btn"
                onClick={() => {
                  console.log("Saving details:", detailData);
                  alert("‚úÖ Day-wise package details saved locally (API connect next)");
                  setDetailFormOpen(false);
                }}
              >
                üíæ Save Details
              </button>
              <button
                className="cancel-btn"
                onClick={() => setDetailFormOpen(false)}
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
