import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { apiURL, baseURL } from "../services/variables.js";
import "./TripComparison.css";

const TripComparison = () => {
  const location = useLocation();
  const { packageId, category } = location.state || {};

  const [mainPackage, setMainPackage] = useState(null);
  const [otherPackages, setOtherPackages] = useState([]);
  const [selectedPackageId, setSelectedPackageId] = useState("");
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [loading, setLoading] = useState(true);

  // üü¶ 1Ô∏è‚É£ Fetch main package + other same-category packages
  useEffect(() => {
    const fetchPackages = async () => {
      try {
        if (!packageId || !category) {
          console.warn("‚ö†Ô∏è Missing packageId or category!");
          setLoading(false);
          return;
        }

        // Fetch current (main) package
        const mainRes = await fetch(`${apiURL}/packages/${packageId}`);
        const mainData = await mainRes.json();

        // Fetch all in same category
        const categoryRes = await fetch(`${apiURL}/packages/category/${category}`);
        const categoryData = await categoryRes.json();

        // Filter out the current one
        const filtered = categoryData.filter((pkg) => pkg._id !== packageId);

        setMainPackage(mainData);
        setOtherPackages(filtered);
      } catch (err) {
        console.error("‚ùå Error fetching packages:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchPackages();
  }, [packageId, category]);

  // üü© 2Ô∏è‚É£ Fetch the selected package when user picks one
  useEffect(() => {
    const fetchSelected = async () => {
      if (!selectedPackageId) return;
      try {
        const res = await fetch(`${apiURL}/packages/${selectedPackageId}`);
        const data = await res.json();
        setSelectedPackage(data);
      } catch (err) {
        console.error("‚ùå Error fetching selected package:", err);
      }
    };

    fetchSelected();
  }, [selectedPackageId]);

  // üïê Loading state
  if (loading) return <p>Loading package comparison...</p>;

  if (!mainPackage)
    return <p>‚ùå Could not load main package for comparison.</p>;

  return (
    <div className="comparison-container">
      <h2>‚öñÔ∏è Compare Trip Packages</h2>

      <div className="comparison-controls">
        <label>Select a package to compare with:</label>
        <select
          value={selectedPackageId}
          onChange={(e) => setSelectedPackageId(e.target.value)}
        >
          <option value="">-- Choose another package --</option>
          {otherPackages.map((pkg) => (
            <option key={pkg._id} value={pkg._id}>
              {pkg.name}
            </option>
          ))}
        </select>
      </div>

      <div className="comparison-table">
        {/* üß≥ Left side: current package */}
        <div className="comparison-card">
          <h3>Current Package</h3>
          <img
            src={
              mainPackage.mainImage
                ? `${baseURL}/${mainPackage.mainImage}`
                : "/fallback.jpg"
            }
            alt={mainPackage.name}
            className="comparison-image"
          />
          <h4>{mainPackage.name}</h4>
          <p><b>üìç Location:</b> {mainPackage.place}</p>
          <p><b>üí∞ Price:</b> ‚Çπ{mainPackage.price}</p>
          <p><b>üìÖ Duration:</b> {mainPackage.days} days</p>
          <p><b>üè® Hotel:</b> {mainPackage.hotel || "N/A"}</p>
          <p><b>üß≠ Category:</b> {mainPackage.category}</p>
        </div>

        {/* üß≥ Right side: selected package */}
        <div className="comparison-card">
          <h3>Selected Package</h3>

          {selectedPackage ? (
            <>
              <img
                src={
                  selectedPackage.mainImage
                    ? `${baseURL}/${selectedPackage.mainImage}`
                    : "/fallback.jpg"
                }
                alt={selectedPackage.name}
                className="comparison-image"
              />
              <h4>{selectedPackage.name}</h4>
              <p><b>üìç Location:</b> {selectedPackage.place}</p>
              <p><b>üí∞ Price:</b> ‚Çπ{selectedPackage.price}</p>
              <p><b>üìÖ Duration:</b> {selectedPackage.days} days</p>
              <p><b>üè® Hotel:</b> {selectedPackage.hotel || "N/A"}</p>
              <p><b>üß≠ Category:</b> {selectedPackage.category}</p>
            </>
          ) : (
            <p>üëà Select a package from the dropdown to compare.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default TripComparison;
