import React, { useState, useEffect } from "react";
import "./Packages.css";

export default function Packages() {
  const [activeTab, setActiveTab] = useState("domestic");
  const [packages, setPackages] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [newPackage, setNewPackage] = useState({
    title: "",
    description: "",
    destination: "",
    category: "domestic",
    duration: "",
    price: "",
    inclusions: "",
    exclusions: "",
    availability: "",
    rating: "",
    status: "active",
    state: "",
    touristPlaces: "",
    images: [],
  });

  // Fetch packages
  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/packages");
      const data = await res.json();
      setPackages(data);
    } catch (err) {
      console.error("Error fetching packages:", err);
    }
  };

  // Add or update
  const handleAddOrUpdatePackage = async () => {
    if (!newPackage.title || !newPackage.price || !newPackage.duration) return;

    // Prepare data for backend
    const payload = {
      ...newPackage,
      price: Number(newPackage.price),
      inclusions: newPackage.inclusions
        ? newPackage.inclusions.split(",").map((i) => i.trim())
        : [],
      exclusions: newPackage.exclusions
        ? newPackage.exclusions.split(",").map((e) => e.trim())
        : [],
      touristPlaces: newPackage.touristPlaces
        ? newPackage.touristPlaces.split(",").map((t) => t.trim())
        : [],
      images: Array.isArray(newPackage.images) ? newPackage.images : [newPackage.images],
    };

    try {
      let res, data;
      if (editingId) {
        res = await fetch(`http://localhost:5000/api/packages/${editingId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        });
        data = await res.json();
        setPackages(packages.map((pkg) => (pkg._id === editingId ? data : pkg)));
      } else {
        res = await fetch("http://localhost:5000/api/packages", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        });
        data = await res.json();
        setPackages([...packages, data]);
      }
      resetForm();
    } catch (err) {
      console.error("Error saving package:", err);
    }
  };

  // Delete
  const handleRemovePackage = async (id) => {
    if (!window.confirm("Are you sure you want to delete?")) return;
    try {
      await fetch(`http://localhost:5000/api/packages/${id}`, { method: "DELETE" });
      setPackages(packages.filter((pkg) => pkg._id !== id));
    } catch (err) {
      console.error("Error deleting:", err);
    }
  };

  // Edit
  const handleEditPackage = (pkg) => {
    setNewPackage({
      ...pkg,
      inclusions: pkg.inclusions.join(", "),
      exclusions: pkg.exclusions.join(", "),
      touristPlaces: pkg.touristPlaces?.join(", ") || "",
      images: pkg.images || [],
    });
    setEditingId(pkg._id);
    setFormOpen(true);
  };

  const resetForm = () => {
    setFormOpen(false);
    setEditingId(null);
    setNewPackage({
      title: "",
      description: "",
      destination: "",
      category: "domestic",
      duration: "",
      price: "",
      inclusions: "",
      exclusions: "",
      availability: "",
      rating: "",
      status: "active",
      state: "",
      touristPlaces: "",
      images: [],
    });
  };

  // Filter by tab
  const filteredPackages = packages.filter((pkg) => pkg.category === activeTab);

  return (
    <div className="packages-container">
      <header className="page-header">
        <h1>üåç Travel Packages</h1>
        <button className="add-new-btn" onClick={() => setFormOpen(true)}>‚ûï Add Package</button>
      </header>

      {/* Tabs */}
      <div className="tabs">
        {["domestic", "international"].map((tab) => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? "active" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Packages */}
      <div className="package-grid">
        {filteredPackages.length > 0 ? (
          filteredPackages.map((pkg) => (
            <div key={pkg._id} className="package-card">
              {pkg.images?.length > 0 && (
                <img src={pkg.images[0]} alt={pkg.title} className="package-img" />
              )}
              <div className="package-info">
                <h3>{pkg.title}</h3>
                <p>{pkg.description}</p>
                <p><strong>üìç {pkg.destination}</strong></p>
                <p><strong>üí∞ {pkg.price}</strong> ‚Ä¢ {pkg.duration}</p>
                <p><strong>‚≠ê</strong> {pkg.rating}</p>
                <div className="card-actions">
                  <button onClick={() => handleEditPackage(pkg)}>‚úèÔ∏è Edit</button>
                  <button onClick={() => handleRemovePackage(pkg._id)}>‚ùå Remove</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No packages found in this category.</p>
        )}
      </div>

      {/* Modal Form */}
      {formOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>{editingId ? "Update Package" : "Add New Package"}</h2>
            <div className="form-grid">
              <input
                type="text"
                placeholder="Title"
                value={newPackage.title}
                onChange={(e) => setNewPackage({ ...newPackage, title: e.target.value })}
              />
              <input
                type="text"
                placeholder="Destination"
                value={newPackage.destination}
                onChange={(e) => setNewPackage({ ...newPackage, destination: e.target.value })}
              />
              <textarea
                placeholder="Description"
                value={newPackage.description}
                onChange={(e) => setNewPackage({ ...newPackage, description: e.target.value })}
              />
              <select
                value={newPackage.category}
                onChange={(e) => setNewPackage({ ...newPackage, category: e.target.value })}
              >
                <option value="domestic">Domestic</option>
                <option value="international">International</option>
              </select>
              <input
                type="text"
                placeholder="Duration"
                value={newPackage.duration}
                onChange={(e) => setNewPackage({ ...newPackage, duration: e.target.value })}
              />
              <input
                type="number"
                placeholder="Price"
                value={newPackage.price}
                onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })}
              />
              <input
                type="text"
                placeholder="Inclusions (comma separated)"
                value={newPackage.inclusions}
                onChange={(e) => setNewPackage({ ...newPackage, inclusions: e.target.value })}
              />
              <input
                type="text"
                placeholder="Exclusions (comma separated)"
                value={newPackage.exclusions}
                onChange={(e) => setNewPackage({ ...newPackage, exclusions: e.target.value })}
              />
              <input
                type="text"
                placeholder="Tourist Places (comma separated)"
                value={newPackage.touristPlaces}
                onChange={(e) => setNewPackage({ ...newPackage, touristPlaces: e.target.value })}
              />
              <input
                type="text"
                placeholder="Image URL"
                value={newPackage.images}
                onChange={(e) => setNewPackage({ ...newPackage, images: [e.target.value] })}
              />
            </div>
            <div className="modal-actions">
              <button onClick={handleAddOrUpdatePackage}>{editingId ? "‚úÖ Update" : "‚ûï Add"}</button>
              <button onClick={resetForm}>‚ùå Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
